css:
@-moz-document domain("developer.mozilla.org") {
body,
main,
.search-wrap.expanded,
.quick-links,
.wiki-main-content,
footer {
	background-color: #222;
	color: #ccc;
}
a {
	color: #397B9A;
}
#main-header, #nav-platform-submenu {
	background-color: #222 !important;
}
#nav-platform-submenu .title {
	color: #ccc;
}
.search-wrap, .search-pane {
	background-color: rgba(255,255,255,.1);
}
.search-wrap input, .search-meta > a {
	color: #ccc;
}
div.column-container .result-list-item > h4 a:link {
	color: hsla(205,85%,74%,1);
}
div.column-container .result-list-item > h4 a:visited {
	color: hsla(205,85%,60%,1);
}
div.column-container .result-list-item mark {
	color: #ccc;
}
.title.see-also {
	background-color: #333 !important;
}
.title.see-also,
.quick-links a {
	color: #999;
}
.overheadIndicator, .warning {
	color: #555;
}
.draft, .experimental, .experimentalBadge, .note, .notice, .prefixBox, .privilegedBadge, .renamed, .todo, .translationInProgress, .warning.warning-review {
	background: hsl(47,100%,10%);
	border-color: hsl(47,100%,28%);
}
.spec-Living,
.spec-Standard {
	color: #0F73CE;
}
#main-header {
	background-color: rgba(255, 255, 255, .05);
}
#main-header a:link {
	color: #ccc;
}
#languages-menu {
	color: #bbb;
}
#toc {
	background-color: rgba(255, 255, 255, .05);
}
pre.syntaxbox,
pre[class*=language-] {
	background-color: rgba(255, 255, 255, .95);
}
.language-css .token.string,
.style .token.string,
.token.entity,
.token.operator,
.token.url {
	background-color: inherit;
}
.text-content thead th {
	background-color: rgba(255, 255, 255, .1);
}
.text-content table,
.text-content td,
.text-content th {
	border-color: #555;
}
.text-content table.standard-table th,
.text-content table.standard-table td {
	border-color: #222;
}
.text-content table.standard-table td {
	box-shadow: inset 0 -1px 0 0 rgba(255, 255, 255, .1);
}
div#compat-desktop {
	background-color: #555;
}
.htab>ul>li {
	background-color: #333 !important;
}
.htab>ul>li.selected {
	background-color: #555 !important;
	border-color: transparent;
}
.htab>ul>li.selected a {
	color: #ccc;
}
table.compat-table td {
	background-color: #333;
	border: 1px solid #555
}
.wiki-block {
	border-top-color: #333333;
}
ul.tags li {
	background: #1E2A2F;
	border-color: #093149;
}
}
