/* -------------------- TYPOGRAPHY STYLES -------------------- */

a:link {
	color: #0086b3;
	line-height: 1.27;
	text-decoration: none;
}
a:visited {
	color: #0086b3;
	text-decoration: none;
}

a:hover {
	color: #333;
	text-decoration: none;
}
a:active {
	color: #333;
	text-decoration: none;
}

b{
	font-weight: 500;
}

h1{
	margin: 15px 0;
}

h2{
	margin-bottom: 0.63em;
}

hr{
	border: 0;
	border-top: 1px solid #d8d8d8;
}

li{
	margin-bottom: 5px;
}

/* -------------------- MAIN BODY STYLES -------------------- */

html, body {
	height: 98%;
}

body {
	color: #666;
	/*background-image: url(http://shizafm.com/assets/img/money.png);*/
	background-color: #f4f4f4;
	font-family: 'Roboto', Helvetica, Arial, sans-serif;
	font-size: 16px;
	line-height: 1.27;
}

.container {
	width: 1170px;
	margin: 0 auto;
}

.content{
	float: left;
	min-height: 1px;
	box-sizing: border-box;
	width: 67.80%;
	margin-right: 0.99%;
}

.content menu{
	float: left;
	min-height: 1px;
	box-sizing: border-box;
	width: 67.80%;
	margin-right: 0.99%;
}

.sidebar{
	float: left;
	min-height: 1px;
	box-sizing: border-box;
	width: 31%;
}

/* -------------------- HEADER STYLES -------------------- */

.header-container {
	width: 670px;
	margin: 0 auto;
}

.header-logo {
  display: none;
}

#age_warning{
  display: none;
}


/* -------------------- CONTEN-NAV STYLES -------------------- */

.nav-shadow{
	background: url("http://shizafm.com/assets/img/sh_0.png") no-repeat;
	width: 34px;
	height: 49px;
	margin-right: -32px;
	margin-top: -54px;
}

.content-nav{
	height: 50px;
}

.content-nav ul{
	float: left;
	margin: 0;
	padding: 0;
	list-style: none;
}

.content-nav ul li {
	display: inline-block;
}

.content-nav ul li:first-child{
	display: none;
}

.content-nav ul li:last-child a{
	border-right: 1px solid #f2f2f3;
	padding-right: 18px;
}

.content-nav a{
	display: block;
	border-left: 1px solid #f2f2f3;
	padding: 15px 14px 14px 18px;
	color: #666;
	text-decoration: none;
}

.content-nav a:hover{
    cursor: pointer;
	color: #999;
	text-decoration: none;
}

.link_button{
	margin-top: -15px;
	opacity: 0.5;
	border: 0!important;
}

.link_button:hover{
	opacity: 0.95;
}

.vk_player{
	margin-top: -15px;
	margin-right: -5px;
	float: right;
	opacity: 0.5;
	border: 0!important;
}

.vk_player:hover{
	opacity: 0.95;
}

/* ---------------- STREAM WARNING AND STREAM VIDEO STYLES ----------------- */

.stream_warning{
	width: 60%;
	max-width: 600px;
	background-color: #FFBBBB;
	padding: 10px 0 10px 0;
	margin-bottom: 5px;
}

.stream_video{
	width: 550px;
	margin: 0 auto;
	margin-bottom: 5px;
	border-style:solid;
    	border-width:25px;
	-webkit-border-image:url(http://shizafm.com/assets/img/tele.png) 13 13 13 13 round;
	-moz-border-image:url(http://shizafm.com/assets/img/tele.png) 13 13 13 13 round;
	-o-border-image:url(http://shizafm.com/assets/img/tele.png) 13 13 13 13 round;
	height:400px;
}

.stream_video_link{
	background-color: #FFBBBB;
    position: relative;
    overflow: hidden;
	width: 30%;
	font-weight: 500;
	border-radius: 3px;
}

.stream_video_link a{
	color: #666;
}

.payment{
	width: 505px;
	margin: 0 auto;

}


/* -------------------- CONTENT STYLES -------------------- */

.main-content{
	padding: 0 28px 10px 28px;
}

.main-content-shadow{
	background: url("http://shizafm.com/assets/img/sh_1.png") no-repeat;
	width: 254px;
	height: 542px;
	margin-right: -254px;
	margin-top: 6px;
}

.work1 {
