/* -------------------- TLEN STYLES -------------------- */

.tlen:before{
    display: block;
    height: 555px;
    border-radius: 3px;
    /* background: url(http://shizafm.com/assets/img/tlen.jpeg); */
	background: url(http://shizafm.com/assets/img/bunker.jpeg);
    background-size: 100%;
    content: "";
}

.tlen .message{
    margin: -301px auto 250px;
    padding: 10px;
    width: 370px;
    border-radius: 5px;
    background: rgba(0, 0, 0, 0.7);
    color: #fff;
    text-align: center;
    font-size: 15pt;
}

@media screen and (max-width: 1200px) {
	.tlen:before { height: 445px; }
	.tlen .message { margin: -251px auto 200px; }
}

@media screen and (max-width: 980px) {
	.tlen:before { height: 345px; }
	.tlen .message { margin: -201px auto 150px; }
}

@media screen and (max-width: 768px) {
	.tlen .message { font-size: 13pt; width: 270px; }
}

