/* -------------------- PLAYER STYLES -------------------- */

.player{
	height: 50px;
}

.track-list ul li{ width: auto; }

.track-title{
	float: left;
	padding: 16px 0 0 15px;
	overflow: hidden;
	white-space: nowrap;
	word-wrap: normal;
	text-overflow: ellipsis;
	font-size: 14px;
	width: 275px;
}

.track-list .plays{
	background: url("http://shizafm.com/assets/img/bullet_play.png") no-repeat 14px;
}

.player .player-controls{
	border-right: 1px solid #f2f2f3;
	height: 100%;
	float: left;
}

.player .bitrates {
	margin-top: 15px;
	margin: 14px 10px 0;
}

.player .bitrates .bitrate{
	background-color: #80d1ef;
	font-size: 13px;
	color: #fff;
	-webkit-border-radius: 4px;
	-moz-border-radius: 4px;
	border-radius: 4px;
	padding: 4px 4px;
	opacity: 0.7;
}

.player .bitrates .bitrate a{
	color: #fff;
}

.player .bitrates .bitrate:hover{
	opacity: 1.0;
}

.player-controls .icon{
	background: url("http://shizafm.com/assets/img/controls.png") no-repeat 0;
	width: 19px;
	height: 19px;
	display: block;
}

.player-controls .icon:hover{
	cursor: pointer;
}

.jp-play, .jp-pause{
	margin: 15px 13px 15px 16px;
}

.jp-play:hover{
	background-position: -20px 0;
}

.jp-pause{
	margin: 15px 14px 15px 15px;
	background-position: -40px 0 !important;
}

.jp-pause:hover{
	background-position: -60px 0 !important;
}

.jp-volume-bar {
	overflow: hidden;
	background-color: #eaeaea;
	border-radius: 3px;
	margin: 7px 0 0 8px;
	width: 80px;
	height: 5px;
	cursor: pointer;
	float: left;
}

.jp-volume-bar-value {
	background-color: #80d1ef;
	border-radius: 3px;
	width: 0px;
	height: 5px;
}

.volume{
	background-position: -160px 0;
    opacity: 0.7;
    float: left;
    margin-left: 15px;
}

