	border-bottom: 1px solid #f2f2f3;
	overflow: hidden;
	white-space: nowrap;
	word-wrap: normal;
	text-overflow: ellipsis;
	font-size: 14px;
	margin: 0;
}

.track-list ul li:last-child{
	margin-bottom: 0;
