@-moz-document domain("steamcn.com") {
body,
.bm,
.pt hm,
.bm_c {
    background: #000
}
.frame-tab .tb-c,
.subforum_left_title {
    background: #BFBFBF;
    color: #FFF;
}
.index_navi_left,
.index_navi_mid,
.index_navi_right,
.subforum_subject2 {
    background: rgb(56, 56, 56)
}
.index_subject2,
.index_subject {
    background: rgb(150, 150, 150);
}
.subject_row_detail_text_up a {
    color: rgb(0, 60, 88);
}
.subforum_subject_detail_text_up {
    color: #FFF
}
#threadlisttableid {
    background: #000;
}
.subforum_subject2 .subforum_subject_detail_text_down .active a {
    color: #FFF
}
a {
    color: #FFF
}
.tl th a:visited,
.tl td.fn a:visited {
    color: #FFF
}
.t_f,
.t_f td {
    color: red
}
/*DISPLAY=NONE*/

#separatorline {
    display: none
}
/*DISPLAY=NONE*/

.tl tr:hover th,
.tl tr:hover td {
    background-color: rgb(56, 56, 56)
}
