@-moz-document url-prefix("http://ehaqui.com/forum") {
	
	.navTabs li:nth-child(2) a.navLink,
	.navTabs li:nth-child(3) a.navLink,
	.navTabs li:nth-child(4) a.navLink,
	.navTabs li:nth-child(5) a.navLink,
	.navTabs li:nth-child(6) a.navLink,
	.navTabs li:nth-child(7) a.navLink {
		width: 30px;
	}
	
	.ugc a, .ugc a:hover {
		color: gray !important;
	}
	
	.ugc a:hover {
		background-color: transparent !important;
		text-decoration: underline !important;
		box-shadow: none !important;
	}
	
	.footer a:hover {
		color: gray !important;
	}
	
	#uix_paneContainer {
		background-color: #070707 !important;
	}
	
	#userBar .navTabs .navTab.Popup.PopupContainerControl.PopupOpen {
		background-color: #050505 !important;
	}
	
	.navTabs .navTab.Popup.PopupContainerControl.PopupOpen {
		background-color: #050505 !important;
	}
	
	/* S U B M E N U */
	
	.navTabs .navTab.selected .tabLinks {
		background-color: #1C1C1F !important;
		color: white !important;
		border: 0px !important;
	}
	
	/* B O T A O   S E L E C I O N A D O */
	
	#navigation .pageContent .navTabs ul.left > li.selected, #navigation .pageContent .navTabs ul.left > li.selected a.navLink {
		background-color: #1C1C1F !important;
		border-color: #1C1C1F !important;
	}
	
	/* B O T A O   H O M E */
	
	#navigation .pageContent .navTabs ul.left > li:nth-child(1) a.navLink {
		color: transparent;
		background: url(https://u.pomf.is/qavdnt.png) !important;
		background-size: 20px 20px !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
	}
	
	/* B O T A O   F O R U N S */
	
	#navigation .pageContent .navTabs ul.left > li:nth-child(2) a.navLink {
		color: transparent;
		background: url(https://u.pomf.is/gysjcu.png) !important;
		background-size: 20px 20px !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
	}
	
	/* B O T A O   L O J A */
	
	#navigation .pageContent .navTabs ul.left > li:nth-child(3) a.navLink {
		color: transparent;
		background: url(https://u.pomf.is/ybcfih.png) !important;
		background-size: 20px 20px !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
	}
	
	/*  B O T A O   S H O U T B O X */
	
	#navigation .pageContent .navTabs ul.left > li:nth-child(4) a.navLink {
		color: transparent;
		background: url(https://u.pomf.is/rozaxa.png) !important;
		background-size: 20px 20px !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
	}
	
	/* B O T A O   R E S O U R C E S */
	
	#navigation .pageContent .navTabs ul.left > li:nth-child(5) a.navLink {
		color: transparent;
		background: url(https://u.pomf.is/waswks.png) !important;
		background-size: 20px 20px !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
	}
	
	/* B O T A O   M E M B R O S */
	
	#navigation .pageContent .navTabs ul.left > li:nth-child(6) a.navLink {
		color: transparent;
		background: url(https://u.pomf.is/qmehji.png) !important;
		background-size: 20px 20px !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
	}
	
	/* B O T A O   F A Q */
	
	#navigation .pageContent .navTabs ul.left > li:nth-child(7) a.navLink {
		color: transparent;
		background: url(https://u.pomf.is/hstron.png) !important;
		background-size: 20px 20px !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
	}
	
	#navigation .pageContent .navTabs {
		background-color: #121314 !important;
	}
	
	#userBar .navTabs {
		background-color: #0B0B0B !important;
	}
	
	.avatar img, .avatar .img {
		border-radius: 50% !important;
	}
	
	.messageUserBlock, .avatarHolder, .userText, .extraUserInfo {
		background-color: #121314 !important;
		border: 0px !important;
		border-radius: 0px !important;
	}
	
	#content, #content .pageContent {
		border-radius: 0px !important;
	}
	
	#content .pageContent {
		background-color: #121314;
		border: 1px solid #000 !important;
	}
	
	.tabs {
		border-bottom: 0px !important;
	}
	
	.tabs li.active a {
		background-color: #121314 !important;
		border-color: black !important;
		color: white !important;
	}
	
	.tabs li:not(.active) a {
		background-color: #292a2b !important;
		border-color: black !important;
		color: white !important;
	}
	
	.funbox {
		display: none;
	}
	
	.titleBar h1 {
		color: white !important;
	}
	
	.breadcrumb .homeCrumb .uix_icon-home:before {
		color: white !important;
	}
	
	.breadcrumb {
		border: 0px !important;
		color: white !important;
	}
	
	.breadcrumb, .crumb {
		background-color: #0B0B0B !important;
		color: white !important;
		border: 0px !important;
	}
	
	.arrow {
		display: none !important;
	}
	
	/*.sp-avatar, .sp-avatar img {
		border-radius: 50% !important;
	}*/
	
	.messageList .message.InlineModChecked {
		background-color: #1C1C1F !important;
	}
	
	.messageList .message {
		background-color: #1C1C1F !important;
		border-radius: 3px !important;
		border: 0px !important;
		color: white !important;
	}
	
	#QuickSearchQuery, #QuickSearch .primaryControls .uix_icon, .textCtrl {
		border: 0px !important;
		background-color: #0b0b0b !important;
		color: white !important;
		border-radius: 3px !important;
	}
	
	input.button.primary {
		border: 0px !important;
		background-color: #0b0b0b !important;
		color: white !important;
		border-radius: 3px !important;
	}
	
	.formPopup .secondaryControls {
		background-color: #121314 !important;
		color: white !important;
	}
	
	.Menu, .Menu .primaryContent, .Menu .secondaryContent, .Menu .sectionFooter, .Popup .PopupControl.PopupOpen {
		background-color: #050505 !important;
		color: white !important;
		border-bottom: 0px !important;
	}
	.Menu {
		border-top: 0px !important;
		border: 0px !important;
		border-radius: 3px !important;
	}
	
	#calroot {
		background-color: #121314;
		color: white;
	}
	
	label {
		color: white;
	}
	
	.hasJs .bbCodeSpoilerText, .hasJs .bbCodeSpoilerText span {
		background-color: #161618 !important;
		color: white !important;
		border-color: #161618 !important;
	}
	
	.message .messageMeta .control {
		color: white !important;
		background-color: #121314 !important;
		border-color: #121314 !important;
	}
	
	/*input[type="checkbox"] {
		filter: invert(1);
	}*/
	
	.bbCodeQuote {
		border-color: black !important;
		border-radius: 0px !important;
	}
	
	.bbCodeBlock * {
		background-color: #0b0b0b !important;
		border-radius: 0px !important;
	}
	
	.bbCodeQuote .attribution, .bbCodeQuote .attribution .AttributionLink {
		color: gray !important;
		border-bottom: 1px solid black !important;
	}
	
	.bbCodeBlock .type {
		color: gray !important;
		border-bottom: 1px solid black !important;
	}
	
	.bbCodeBlock {
		border-color: black !important;
		border-radius: 0px !important;
	}
	
	.bbCodeQuote .attribution, .bbCodeQuote .quoteContainer {
		border-left: 2px solid black !important;
	}
	
	.message .likesSummary {
		background-color: #121314 !important;
		color: gray !important;
		border: 0px !important;
	}
	
	.tagBlock{
		background-color: #1C1C1F !important;
		border: 5px solid #1C1C1F;
		border-radius: 3px;
	}
	
	.PanelScroller .Panels * {
		background-color: #232323 !important;
	}
	
	.PanelScroller .scrollContainer {
		border: 0px !important;
	}
	
	.PanelScroller .navControls a.current {
		background-color: #232323 !important;
		border: 0px !important;
		color: gray !important;
	}
	
	.PanelScroller .navControls a:not(.current) {
		background-color: #383838 !important;
		border: 0px !important;
	}
	
	.PanelScroller .panel {
		color: white !important;
	}
	
	.PageNav a.text, .PageNav a {
		color: white !important;
		background-color: #0b0b0b !important;
		border-color: #0b0b0b !important;
	}
	
	.PageNav a.text:hover, .PageNav a:hover {
		color: gray !important;
	}
	
	.mainContentBlock, .mainContentBlock a {
		background-color: #1C1C1F !important;
		border-color: #1C1C1F !important;
		color: white !important;
	}
	
	.navigationSideBar a, .navigationSideBar a:hover {
		background-color: #1C1C1F !important;
		color: gray !important;
	}
	
	.navigationSideBar > ul {
		border: 0px !important;
		background-color: #1C1C1F !important;
	}
	
	.subHeading {
		color: gray !important;
		background-color: #1C1C1F !important;
		border: none !important;
	}
	
	.heading {
		background-color: #0b0b0b !important;
	}
	
	a.callToAction span {
		background-color: #0b0b0b !important;
	}
	
	.uix_breadCrumb_toggleList li.toggleList_item {
		background-color: #0b0b0b !important;
		border-color: #0b0b0b !important;
	}
	
	.secondaryContent {
		background-color: #1C1C1F !important;
		color: white !important;
		border-color: #1C1C1F !important;
	}
	
	.secondaryContent h3 {
		background-color: #0b0b0b !important;
		color: white !important;
		border-color: #0b0b0b !important;
	}
	
	#forums.nodeList li {
		background: #1C1C1F !important;
	}
	
	.nodeList li .nodeInfo {
		background-color: #1C1C1F !important;
		color: white !important;
		border: 0px !important;
		border-radius: 3px !important;
	}
	
	.nodeList li .nodeInfo a {
		color: gray !important;
	}
	
	.nodeList {
		border: 0px !important;
		border-radius: 3px !important;
	}
	
	.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
		background: #1ED760 url('https://u.pomf.is/fxmqkm.png') no-repeat 0 0;
	}
	
	.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
		background: #121314 url('https://u.pomf.is/fxmqkm.png') no-repeat 0 0;
	}
	
	.footer .pageContent {
		background-color: #121314 !important;
		border-top-color: black !important;
		border-bottom-color: black !important;
	}
	
	.footer .choosers a, .footer .choosers a:hover {
		background-color: #0b0b0b !important;
		color: white !important;
		border-color: #0b0b0b !important;
	}
	
	.discussionList .sectionHeaders dt, 
	.discussionList .sectionHeaders dd,
	.discussionList .discussionListItem,
	.discussionListItem .posterAvatar,
	.discussionListItem .stats,
	.discussionListItem .noteRow {
		background-color: #1C1C1F !important;
		border-color: black !important;
	}
	
	.discussionList a {
		color: gray !important;
	}
	
	.discussionList .sectionHeaders {
		border-color: black !important;
	}
	
	.discussionList .sectionFooter {
		background-color: #0b0b0b !important;
		color: gray !important;
		border-bottom: 1px solid black !important;
	}
	
	.primaryContent {
		background-color: #1C1C1F !important;
		color: white !important;
		border-bottom: 1px solid black !important;
	}
	
	.messageSimple .avatar {
		padding: 10px 0 0 10px !important;
	}
	
	.navPopup .PopupItemLinkActive:hover {
		background-color: #0b0b0b !important;
	}
	
	#redactor_modal_overlay, #exposeMask {
		background-color: black !important;
	}
	
	.bbCodeSpoilerButton {
		background-color: #0b0b0b !important;
		color: white !important;
	}
	
	.bbCodeSpoilerButton:hover {
		background-color: #050505 !important;
		color: white !important;
	}
	
	.resourceListMain {
		background-color: #1C1C1F !important;
		border-color: #1C1C1F !important;
	}
	
	.resourceList li {
		margin-top: -4px !important;
	}
	
	.resourceList li, .resourceListItem .resourceImage, .resourceListItem .resourceStats {
		background-color: #121314 !important;
		border-color: #121314 !important;
		color: white !important;
		border-radius: 3px !important;
	}
	
	.resourceListItem .resourceImage .resourceIcon img {
		background-color: #1C1C1F !important;
	}
	
	#taigachat_box {
		background-color: #121314 !important;
		border-color: #121314 !important;
	}
	
	.taigachat_motd, #taigachat_motd {
