css:
@-moz-document domain("web.whatsapp.com") {
body
{
    background:#181818;
}


.avatar
{
    background: url('http://www.clipartbest.com/cliparts/MTL/L5p/MTLL5pRBc.png');
    background-size: 80% 80%;
    background-position: center center;
    background-repeat: no-repeat;
}

.msg
{
    padding-left:2%;
    padding-right:2%;
}

.pane-intro {
    background: transparent;
}
.pane-intro:after {
    border:none;
}
/* intro */
.pane-intro .intro-image {
    background-image: url('http://www.clipartbest.com/cliparts/MTL/L5p/MTLL5pRBc.png');
    background-color:white;
    background-size: 95%;
    padding:15px;
    border-radius:5px;
}
.intro-body
{
    padding: 50px;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 5px;
    box-shadow: 5px 5px 6px 0px rgba(0,0,0,0.5);
}
.app-wrapper-web .app {
    width: 100%;
    top: 0;
    height: 100%;
    min-width: auto;
}
.app-wrapper-main.app-wrapper {
    min-width: 0;
    min-height: 0;
}
.app-wrapper::after {
    background: transparent;
}

.app.two
{
    background: transparent;
}
.page-chat
{
    background: transparent;
}
.pane-chat-tile-container {
    background-image: url(http://www.justinmaller.com/img/projects/wallpaper/WP_Daft_Punk-2560x1440_00000.jpg);
    background-size: cover;
    background-position:center center;
}
.pane-chat-tile {
    background: transparent;
}
footer {
    bottom: 0px;
    position: absolute;
    background-color: white;
}
#side {
    left: calc(100% - 70px);
    width: 250px !important;
    position: absolute;
    z-index: 9999;
    transition: all 0.1s 0.5s;
}
#side:hover {
    left: calc(100% - 250px);
}
#main {
    width: calc(100% - 70px);
    left: 0px;
}
.drawer-container-right,
.pane-chat,
.pane-intro {
    background: transparent;
}
.unread {
    background-color: #A2F5B6 !important;
}
.ui .app-wrapper {
    min-width: 0px;
}
.chat.active {
    background-color: #CBC9D1 !important;
}
.chat.active:hover {
    background-color: #BEBCC4 !important;
}
.chat:hover {
    background-color: #E2E1E6 !important;
}
.ptt-container {
    display: none;
}

.pane-chat-footer
{
    background-color:rgba(255, 255, 255, 0.8);
}
}
