css:
@-moz-document domain(jeuxvideo.com) {

/* Profils */ 
.content-img-avatar {
    border-radius: 75px 75px 75px 75px;
}

.content-img-avatar img {
    border-radius: 75px 75px 75px 75px;
}



/*  Publicités */
.bonus-track {
    display: none;
}

#forum-right-col .show-pub {
    display: none;
}

.show-pub {
    display: none;
}

#prospect-interstice {
    display: none;
}



/* Corps du site */
body {
    background: url(http://image.jeuxvideo.com/css_img/defaut/body_bg2.png) repeat-x scroll 0 0 #D7D7D7;
}

.header-top {
    background: #46444b;

}

.header-bottom, .header-sticky {
    background-color: #333;
    border-bottom: 1px solid #46444b;
    /* border-bottom: 1px solid #46444b; J'ai retiré psk c'est moche sans le menu */
}

.header-sticky .nav-platform {
    background-color: #46444b;
}

.header-sticky .nav-platform .lvl2 {
    background-color: #46444b;
}

.header-logo:after {
    display: none;
}

.game-top {
    background: #333 !important;
}

.navbar-game {
    background: #46444b;
}

.label-support {
    background-color: #46444b;
}

.label-support.active, .label-support.active-mach-version {
    background: #325361;
}

#content > div:nth-child(5) {
    min-height: 20px !important;
    height: 20px !important;
    visibility: hidden !important;
}



/* Footer */
.footer-links {
	display:none;
}

#jv-footer .footer-mentions {
    background: #333;
}

#jv-footer .footer-social-network {
    background-color: #46444b;
    color: #FFF;
}

#jv-footer .footer-social-network .footer-social-network-link {
    background-color: #333;
}



/* Blocs à droite */
.panel-jv-forum .panel-heading {
    background-color: #325361;
    border: 0 solid #2a4855;
}

.panel-jv-forum .panel-heading + .panel-body {
    border-top: 0 none;
    background: none repeat scroll 0 0 #EDEDED;
    color: #000;
}

.panel-jv-forum .panel-heading + .panel-body a {
    color: #000;
}

.panel-jv-forum .panel-heading + .panel-body a:hover {
    color: #F50 !important;
}

.panel-jv-forum .liste-modo-fofo {
    color: #000;
    font-weight: 700;
}


.panel-jv-forum
.titre-info-fofo {
    color: #F50;
}



/* Divers */
.bloc-pagi-default a:visited {
    color: grey;
}

.bloc-sous-forum-header {
    background: none repeat scroll 0 0 #9c0;
    border-bottom-left-radius: 20px;
    border-top-right-radius: 20px;
}

.bloc-sous-forum-header
.liste-sous-forum {
    background: #9c0;
}

.bloc-sous-forum-header
.liste-sous-forum a {
    color: #fff;
}

.btn-actu-new-list-forum {
    background: none repeat scroll 0 0 #4b7c92;
    position: left;
}

.btn-actu-new-list-forum:hover {
    background: none repeat scroll 0 0 #477387;
}

.btn-col-right-light {
    background: #9c0;
}

.btn-col-right-light:hover {
    background: #6A6;
}

.btn-topic-resolu {
    border: 0.0625rem solid #99CC00;
    background: #99CC00;
}

.btn-topic-resolu:hover {
    border: 0.0625rem solid #6A6;
    background: #6A6;
}

#barre-moderation .btn {
    cursor: pointer;
    margin: 0 2px 10px;
    background: #4b7c92;
}

#barre-moderation {
    background: rgba(255,255,255,0.8);
    box-shadow: 0px 5px 5px -3px rgba(0,0,0,0.14),0px 8px 10px 1px rgba(0,0,0,0.098),0px 3px 14px 2px rgba(0,0,0,0.082);
    border-bottom: 5px solid #4b7c92;
}

#aes-title.btn {
    background: none repeat scroll 0 0 #9c0;
    padding: 4px 15px;
}

.btn-ajouter-sondage {
    background: none repeat scroll 0 0 #9c0;
    border: 0;
}

.btn-ajouter-sondage:hover {
    background: none repeat scroll 0 0 #6A6;
}

.btn-ajouter-spotify {
    background: none repeat scroll 0 0 #9c0;
    border: 0;
}

.boutons-spotify-topic {
    border-radius: 0;
    border: 0;
}

.btn-ajouter-spotify:hover {
    background: none repeat scroll 0 0 #6A6;
}

.bloc-message-forum-large .bloc-date-msg {
    float: right;
}

.btn-poster-msg {
    background: none repeat scroll 0 0 #9c0;
}

.btn-poster-msg:hover {
    background: none repeat scroll 0 0 #6A6;
    color: #FFF;
}



/* Commentaires*/
/* Couleurs des + et des - */
.moins-commentaire {
    color: #F60;
}

.plus-commentaire {
    color: #06C;
}



/* Bg bloc thead owned fdp de merde fils de pute de bg tu m'a bien fait chier mais je t'ai eu hein hein connard CONNARD JE TE HAIS */
#table-liste-topic-forum thead {
    background: #f4f4f4;
}

}
