css:
@-moz-document domain("tatoeba.org") {
html, body {
	background: #252525;
    color: #D0C9B1;
}

input, select, textarea {
    border: 1px solid #101010!important;
    background: #1D1D1D!important;
    color: #D0D0D0!important;
}

fieldset, #UserLoginForm, #UserRegisterForm, #UserNewPasswordForm {
	background: #252525;
    border: 1px solid #2D2D2D!important;
}

.user, .currentContributor, .activityBar, .usersLanguages .languageLevel, .usersLanguagesStats .languageLevel, .usersLanguagesStats tr, .sentences_set .menu, #footer .category h3, .lastWallMessages .body, .profileDescription, .currentPicture, #save_basicForm, #quiz, #termsOfUse {
	border-color: #444;   
}

.downloadLink, .registerLink, .topContent .descriptionBar, #new-search-bar .search-submit-button, .titleAnnexeLink, .form .button.submit, .search_bar .search-submit-button, .editOption a, .submit input {
    color: #252525!important;
    border-color: #580!important;
    background: #580!important;
}

#new-search-bar .search-submit-button:hover, .downloadLink:hover, .registerLink:hover, .titleAnnexeLink:hover, .search_bar .search-submit-button:hover, .editOption a:hover, .submit input:hover, .addTranslation_buttons .submit:hover, .button.submit:hover {
    background: #DD8800!important;
    border-color: #DD8800!important;
    color: #252525!important;
}

.button.cancel {
    color: #ddd;
    background-color: #111!important;
    border-color: #111!important;
}
.button.cancel:hover {
    background-color: #454545!important;
    border-color: #454545!important;
}


/* top menu bar */

#top_menu a, #top_menu div, #UserLoginForm_FromBar {
	color: #d5d5d5;
}

#UserLoginForm_FromBar {
	border: none;
}

/* top content */
.topContent {
    background: #252525;
}
.topContent .descriptionBar {
	background: #580;
}

/* content */
#content {
    background: #303030!important;
    color: #D0C9B1!important;
    border-bottom: none
}

.module {
 	background: #292929!important;
    border-bottom: none;
    color: #D0C9B1;
}

/* main page search bar */

#new-search-bar > fieldset, #SentenceSearchForm > fieldset{
    background: #252525;
    border: none!important;    
}

#new-search-bar .extra-links em {
	color: skyblue;
}

/* global search bar */
.search_bar {
	background: #252525;
    color: #AAA;
}
.search-bar-extra {
	color: #580;
}

.search-bar-extra a {
	color: #580;
}
.search-bar-extra a:hover {
	color: #DD8800;
}

/* navigation */

.paging a {
	background: #462;
	color: inherit;
}
.paging .disabled {
    background: #2B381E;
    color: #546D3B;
}

.paging .current {
    color: #333;
    background: #69AA27;
}

.paging a:hover {
	background: #DD8800;
}

.options .active {
	background: #69AA27;
}
.options .active a {
    color: #333;
}
.options .active:hover {
	background: #DD8800;
}

/* sentences */
.match {
    background: #634128;
}

.transcriptions .transcription, .transcription ruby rt {
	color: #67614C;   
}

.sentence:hover {
    /*background: #7BAD52;*/
    background: #3E552B;
    color: black!important;
}

.sentence:hover .transcriptions .transcription, .sentence:hover .transcription ruby rt {
    color: #2D2D2D;
}

/* sentence page */
.sentences_set .menu .option a:hover, .sentences_set .menu .option:hover, .sentences_set .link.button:hover {
    color: #7BAD52;
}

#logs .linkLog.added.hovered {
    background-color: #557;
    border-right-color: #778;
}

.tag {
	background: #9E6D38;
    border-color: #6E3D08;
}
.tag a {
	color: #333;
}
.tag a:hover {
	color: #DDD;
}

.numSentences {
    color: #484848;
}

div.showLink {
    background: rgba(0, 102, 170, 0.5);
    color: #D0D0D0;
}

div.hideLink {
    background: rgba(0, 102, 170, 0.5);
    color: #D0D0D0;
}


/* activity timeline */

#timeline .date {
    color: #999;
}

#timeline .number {
	color: #bbb;
}

#timeline td, #timeline .number {
	border-color: #444
}

#annexe_content .selected a {
	color: #B8EB84;
}


/* languages */

.usersLanguagesStats .languageLevel .unknownLevel {
	background: #1A1A1A;
    color: #bbb;
}

.usersLanguagesStats .selected {
	background: #3E552B;
    color: #B8EB84;
}

.languages-stats tr {
	border-color : #444;
}


/* users profile */

.profileSummary, .profileLanguages {
	border-color : #252525;   
}

.form {
 	background: #252525;
    border: none;
}
}

@-moz-document domain("tatoeba.org") {
/* MESSAGE */

.message, .message .body, .message .body .sentence, .inappropriate .separator, .inappropriate .username a, .inappropriate .menu a {
    border-color: #444;
}


.message .header a {
    color: #151515;
    background-color: #462;
    border-color: #462;
}

.message .header a:hover {
    background-color: #D80;
    border-color: #D80;
}

.message .avatar {
    border-color: #462;
    background-color: #462;
}


.message .pm a, .message .pm a:hover {
    background-color: inherit;
}

a.ownerName {
    color: #580;
}

.message .body .sentenceText {
    color: #555;
}

.message .body .sentence:hover .sentenceText {
    color: #151515;
}


.message.form .body, .message.form .avatar {
    background-color: #252525;
}

.message.form .avatar {
	border-color: #444;
}


/* THREADS */

.thread {
	border-color: #444;
}

.topThread {
	border-color: #444;
    border-top: solid 1px #444;
}

.root .message .header {
	border-right: 0px;
}

.wall .showReplies,
.wall .hideReplies {
    background: rgba(0, 102, 170, 0.5);
    color: #D0D0D0;
    border: #D0D0D0;
}
}

@-moz-document url-prefix("http://en.wiki.tatoeba.org") {
h1, h2, h3, h4, h5 {
    color: #66AA00;
    border-color: #444!important;
}

a {
   color: #66AA00;
}

a:hover {
    color: #DD8800;
}

#container1 {
    background: #303030;
}

#annexe_content {
	background: #252525;
}

#footer {
	background: #151515;
    color: white;
}
}
