css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://fallout2online.com/forum") {
  
  *{
    background: none !important;
    color: #ADB !important;
    border-width: 1px !important;
    border-color: #333 !important;
    border-radius: 0px !important;
    text-shadow: 0 0 0 transparent, 0 0px 0px rgba(0,0,0,0.0) !important;
    box-shadow:  0 0 0 transparent, 0 0px 0px rgba(0,0,0,0.0) !important;
  }
  
  .externalLink, .internalLink {
    text-decoration: underline !important;
  }
  
  .externalLink:hover, .internalLink:hover {
    text-decoration: underline !important;
    color: #DFB !important;    
  }
    
  h3 * {
    color: #DFB !important;
  }
  
  .pageWidth {
    margin: 0% 2%;
  }
  
  .pageContent {
    margin: 0;
    padding: 0;
  }
  
  #headerProxy{
    height: 64px !important;;
  }
  
  .xenOverlay, .Menu, .secondaryControls{
    background-color: black !important;
    border-width: 1px !important;
    border-color: #888 !important;
  }
  
  body, .Tooltip, .Popup, .xenTooltip, #calroot, .mce-tooltip, .mce-container, .mce-panel, .xenPreviewTooltip {
    background-color: black !important;
  }
  
  #forums, #forums *, #ProfilePostList>*, .breadcrumb, .sidebar, .secondaryContent, .message, .messageUserBlock, .avatarHolder img, .messageList{
        border: 0px solid rgb(0, 0, 0) !important;
  }
  
  .mce-btn {
    border: 0px solid rgb(0, 0, 0) !important;
  }
  
  .message {
    padding: 0px !important;
    margin: 0px !important;
    min-height: 64px !important; 
  }
  
  
  .message *{
    padding: 1px  !important;
    margin: 0px  !important;
  }
  
  .messageContent .messageText{
    font-family: Helvetica, Tahoma, Arial, sans-serif  !important; 
    line-height: 1.0em !important;
    font-size: 10pt;
  } 
  
  .message .messageUserInfo {
    width: 96px !important;
  }
  
  .message .avatar img{
    width: 64px;
    height: 64px;
  }
  
  .nodeIcon, .loginButton, .arrow, .message .extraUserInfo, .breadcrumb, .nodeStats, .discussionList .secondRow, .previewTooltip .avatar, #logoBlock, .breadBoxTop, .message .helper  {
    display: none !important;
  }
  
  .onlineMarker {
    top: 3px !important;;
    left: 3px !important;;
    background-color: #3F6 !important;;
    width: 4px;    
    height: 4px;
    opacity: 0.8;
    border-width: 0px !important;;
    
  }
  
  .previewTooltip .text{
    margin-left: 0px;
  }
  

  .likesSummary, .messageMeta, .editDate, .message .userText  {
    opacity: 0.0;
  }
  
   .message:hover .messageMeta, .message:hover .likesSummary, .message:hover .editDate, .profilePostListItem:hover .messageMeta, .message:hover .userText, .tabLinks {
    opacity: 0.55;
  }

  .button {
    border: 1px solid #888 !important;
    box-shadow: 0px 0px 0px 0px rgb(0,0,0) !important;
  }
  
}
