css:
@-moz-document domain(learn.lds.org) {
  .d2l-background-global, #d2l_navbar div.d_nb_cGlobal, ul.d2l-datalist.vui-list, div.dco_c, .d2l-widget-content, li, .d2l-collapsepane-header, .d2l-menuflyout-contents, .d2l-msg-container, .d2l-msg-container-inner, .d2l-page-main-padding, .vui-tabmenu>.vui-tabmenu-item-select>a, .d2l-page-bg>div, .d2l-page-collapsepane-content-inner, .d2l-page-collapsepane-content, .d2l-menuflyout-link.d2l-menuflyout-open .d2l-menuflyout-link-link, .d2l-page-main, .d2l-twopanelselector-wrapper, .SI_Submitted, .d2l-collapsepane-content {
    background-color: black !important;
  }
  
  path {
    fill: #000 !important;
  }
  
  .d2l-minibar, .d2l-widget-header, .d2l-menuflyout.d2l-menuflyout-open .d2l-menuflyout-opener, .SI_TEMPLATE_PART button, .inline-discussion-body .btn-primary, .d2l-page-buttons, .dba_b.d2l-page-buttons-float {
    background-color: darkslategray !important;
  }
  
  .vui-button, .vui-outline, div.container-fluid.inline-discussion-container, .modal-content, textarea.reply-topic-text, input, textarea, .d2l-le-TreeAccordionItem-wrapper {
    background: linear-gradient(to bottom, black 0, black 100%) !important;
    color: darkgray !important;
  }
  
  .vui-outline {
    background: linear-gradient(to bottom, black 0, #262626 100%) !important;
  }
  
  span, .SI_TEMPLATE_FULL, .SI_TEMPLATE_PART .SI_ASSIGN, .SI_ASSIGN textarea, .inline-discussion-post, .d2l-right>*, .d2l-placeholder>*, .d2l-textblock {
    background-color: black !important;
    color: darkgray !important;
  }
  
  #d2l_navbar div.d_nb_cFull {
    background-color: #171717;
  }
  
  ul.d2l-datalist.vui-list {
    border-color: #171717 !important;
  }
  
  .d2l-rowshadeonhover:hover, .d2l-rowshadeonhover-selected {
    background: slategray !important;
  }
}
