css:
@-moz-document domain("www.emudesc.com") {
  html {
    background: rgb(20, 20, 20);
}
  body {
    color: rgb(235, 235, 235);
}
  
  .pageWrapper {
    background-color: rgb(20, 20, 20);
} 
  .navTabs .navTab.selected .tabLinks a {
    color: rgb(129, 129, 129);
}
  #content .pageContent {
    background-color: rgb(20, 20, 20);
}
  #QuickSearch {
    background-color: rgb(20, 20, 20);
}
  .formPopup {
    background-color: rgb(20, 20, 20);
}
  .titleBar {
    color: rgb(86, 86, 86);
}
  .pageWrapper {
    border: 5px solid rgb(51, 50, 50);
}
  .textCtrl {
    color: rgb(255, 255, 255);
    background: rgb(72, 72, 72);
    border-top-color: rgb(72, 72, 72);
    border-right-color: rgb(72, 72, 72);
    border-bottom-color: rgb(72, 72, 72);
    border-left-color: rgb(72, 72, 72);
}
  .textCtrl:focus, .textCtrl.Focus {
    background: rgb(72, 72, 72);
    border-top-color: rgb(72, 72, 72);
    border-bottom-color: rgb(72, 72, 72);
}
  
 .secondaryContent {
    background-color: rgb(20, 20, 20);
    border-bottom: 1px solid rgb(72, 72, 72);
}
  .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    color: rgb(222, 222, 222);
    background-color: rgb(72, 72, 72);
}
 .sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: rgb(222, 222, 222);
} 
  .sidebar {
    color: rgb(222, 222, 222);
  }
  .sidebar .secondaryContent {
    border: 1px solid rgb(72, 72, 72);
}
  .sectionMain {
    background-color: rgb(10, 10, 10);
    border: 1px solid rgb(10, 10, 10);
    -webkit-box-shadow: none;
    -khtml-box-shadow: none;
    box-shadow: none;
}
 .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: rgb(34, 34, 34);
    border-bottom: 1px solid rgb(86, 86, 86);
}
  .node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url('http://orig15.deviantart.net/5889/f/2016/076/a/2/pads2_by_znkhucast-d9vhmm8.png');
}
  .node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
    background-image: url('http://orig15.deviantart.net/5889/f/2016/076/a/2/pads2_by_znkhucast-d9vhmm8.png');
}
  .node .nodeLastPost {
    background-color: rgb(41, 41, 41);
    border-bottom: 1px solid rgb(72, 72, 72);
    border: 1px solid rgb(72, 72, 72);

}
  .avatar img, .avatar .img, .avatarCropper {
    background-color: rgb(34, 34, 34);
    border: 1px solid rgb(70, 70, 70);

}
  .primaryContent {
    background-color: rgb(20, 20, 20);
    border-bottom: 1px solid rgb(72, 72, 72);
}
  
  .Menu .primaryContent {
    background-color: rgb(41, 41, 41);
}
  
  .Menu .secondaryContent {
    background-color: rgb(41, 41, 41);
}
  .Menu .sectionFooter {
    background-color: rgb(41, 41, 41);
}
  .sectionFooter {
    color: rgb(99, 138, 31);
    background: rgb(41, 41, 41);
    border-bottom: 1px solid #a5cae4;
}
  .sectionFooter a {
    color: rgb(99, 138, 31);
}
  .blockLinksList a, .blockLinksList label {
    color: rgb(222, 222, 222);
}
  .blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    background-color: rgb(30, 30, 30);
}
    .navPopup .PopupItemLinkActive:hover {
    background-color: rgb(30, 30, 30);
}
  .navPopup .listPlaceholder ol.secondaryContent.Unread {
    background-color: rgb(69, 68, 68);
}
  
  .messageUserBlock {
    background: rgb(28, 28, 28);
    border: 1px solid rgb(68, 68, 68);
}
  .messageUserBlock div.avatarHolder {
    background-color: rgb(36, 36, 36);
}
  .messageUserBlock .arrow {
    border-left-color: rgb(58, 58, 58);
    -moz-border-left-colors: rgb(58, 58, 58);
}
  .messageUserBlock .extraUserInfo {
    background-color: rgb(36, 36, 36);
}
  .messageUserBlock .arrow span {
    border-left-color: rgb(36, 36, 36);
    -moz-border-left-colors: rgb(36, 36, 36);
}
  .messageList .message:nth-child(2n) {
    background-color: rgb(29, 28, 28);
}
  .conversation_view .messageList {
    border: 1px solid rgb(72, 72, 72);
}
  .messageList .message {
    border-top: 1px solid rgb(72, 72, 72);
}
  
  .messageUserInfo a.avatar {
	background-color: rgb(34, 34, 34) !important;
	border: 1px solid rgb(70, 70, 70) !important;
}
  .message .likesSummary {
    border: 1px solid rgb(54, 54, 54);
}
  .bbCodeQuote .attribution {
    color: rgb(33, 116, 156);
    border-bottom: 1px solid rgb(71, 71, 71);
    background: rgb(54, 54, 54);
    box-shadow: none;
}
  .bbCodeQuote .quoteContainer {
    background: rgb(29, 28, 28);
}
  .bbCodeQuote {
    border-color: rgb(58, 58, 58);
}
  .bbCodeBlock {
    border: 1px solid rgb(58, 58, 58);
}
  .bbCodeBlock .type {
    border: 1px solid rgb(58, 58, 58);
  }
  .bbCode > dl > dd {
    border: 1px solid rgb(47, 47, 47);
    background: rgb(34, 34, 34);
}
  .hasJs .bbCodeSpoilerText {
    background-color: rgb(45, 45, 45);
    border: 1px solid rgb(57, 57, 57);
}
  .bbCodeBlock .type {
    color: rgb(33, 116, 156);
    border: 1px solid rgb(71, 71, 71);
    border-bottom-color: rgb(71, 71, 71);
    background: rgb(54, 54, 54) !important;
    -webkit-box-shadow: none;
    -khtml-box-shadow: none;
    box-shadow: none;
}
  .bbCodeBlock pre, .bbCodeBlock .code {
    background: rgb(58, 58, 58);
}
  
  .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    border: 1px solid rgb(71, 71, 71);
}
  .bbCodeQuote .attribution {
    border-bottom: 1px solid rgb(71, 71, 71);
}
  .navigationSideBar .heading {
    background-color: rgb(26, 85, 113);
    border-bottom: 1px solid rgb(71, 71, 71);
}
  .navigationSideBar a:hover {
    background: rgb(24, 24, 24);
    color: rgb(99, 138, 31);
    text-decoration: underline;
}
  textarea.textCtrl:focus {
    background-image: url('http://i.imgur.com/tphOPHy.png');
}
  .breadcrumb {
    border: 1px solid rgb(70, 70, 70);
    background: rgb(60, 60, 60) !important;;
    -webkit-box-shadow: none;
    -khtml-box-shadow: none;
    box-shadow: none;
}
  .breadcrumb .crust a.crumb {
    color: rgb(147, 170, 209);
}
  .PageNav a {
    color: rgb(59, 111, 144);
    background-color: rgb(45, 45, 45);
    border: 1px solid rgb(57, 57, 57);
}
  .PageNav a:hover, .PageNav a:focus {
    color: rgb(59, 111, 144);
    text-decoration: none;
    background-color: rgb(80, 80, 80);
    border-color: rgb(57, 57, 57);
}
  .quickReply {
    border-top: 1px solid rgb(54, 54, 54);
}
  #QuickReply {
    background: rgb(17, 16, 16);

}
  .discussionListItem {
    background-color: rgb(34, 34, 34);
    border-bottom: 1px solid rgb(86, 86, 86);
}
  .discussionListItem .posterAvatar, .discussionListItem .stats {
    background-color: rgb(34, 34, 34);
}
  .discussionListItem .stats dl {
    padding: 0 10px;
    border-left: 1px solid rgb(86, 86, 86);
    border-right: 1px solid rgb(86, 86, 86);
}
  .navPopup .listItem {
    border-bottom: 1px solid rgb(86, 86, 86);
}
  .textHeading, .xenForm .sectionHeader {
    color: rgb(255, 255, 255);
    border-bottom: 1px solid rgb(86, 86, 86);
}
  .formPopup .controlsWrapper {
    background: rgb(24, 24, 24);
}
  .threadListItem {
    border-top: 1px solid rgb(86, 86, 86);
}
  .profilePostListItem {
    border-top: 1px solid rgb(86, 86, 86);
}
  #DiscussionListOptionsHandle a {
    color: rgb(90, 90, 90);
    background-color: rgb(11, 10, 10);
    border: 1px solid rgb(11, 10, 10);
    border-top: 1px none rgb(11, 10, 10);
}
  .modsInForums {
    border: 1px solid rgb(86, 86, 86);
}
  body .itemPageNav a {
    color: rgb(62, 133, 177);
    background: rgb(56, 52, 52);
    border: 1px solid rgb(86, 86, 86);
}
  body .itemPageNav a:hover {
    color: rgb(62, 133, 177);
    text-decoration: none;
    background-color: rgb(116, 108, 108);
    border-color: rgb(123, 123, 123);
}
  .tabs {
    background: rgb(20, 20, 20);
    border-bottom: 1px solid rgb(86, 86, 86);
}
  .tabs li.active a, .tabs.noLinks li.active {
    background-color: rgb(89, 89, 89);
}
  .tabs li a, .tabs.noLinks li {
    color: rgb(233, 231, 231);
    background: rgb(60, 60, 60);
    border: 1px solid rgb(153, 153, 153);
}
  .chooserColumns li a:hover {
    background-color: rgb(30, 30, 30);
}
  .discussionListItem .noteRow {
    background-color: rgb(20, 20, 20);
    color: rgb(65, 127, 152);
}
  .sectionFooter {
    border-bottom: 1px solid rgb(86, 86, 86);
}
  .profilePage .tabs.mainTabs li.active a {
    background-color: rgb(89, 89, 89);
}
  .profilePage .tabs.mainTabs {
    background: rgb(20, 20, 20);
}
  .profilePage .mast {
    border-right: 1px solid rgb(86, 86, 86);
}
  .profilePage .mast .section.infoBlock {
    border: 1px solid rgb(86, 86, 86);
}
  .profilePage .mainProfileColumn {
    border-left: 1px solid rgb(86, 86, 86);
}
  .profilePage .mast .followBlocks .primaryContent.avatarHeap {
    border: 1px solid rgb(86, 86, 86);
}
  .profilePage .mast .sectionFooter {
    border-left: 1px solid rgb(86, 86, 86);
    border-right: 1px solid rgb(86, 86, 86);
}
  .messageNotices li {
    background: rgb(30, 30, 30);
    border: 1px solid rgb(86, 86, 86);
}
  .textWithCount.subHeading .count {
    border: 1px solid rgb(90, 126, 27);
    background-color: rgb(70, 96, 26);
    color: rgb(224, 222, 222);
}
  .profilePage .primaryUserBlock {
    border-top: 1px solid rgb(86, 86, 86);
}
  .profilePage .primaryUserBlock .lastActivity {
    border-top: 1px solid rgb(86, 86, 86);
}
  .messageSimpleList .placeholder .placeholderContent {
    border: 1px solid rgb(86, 86, 86);
    background: rgb(30, 30, 30);
}
  .thread_view .threadAlerts {
    border: 1px solid rgb(86, 86, 86);
    background-image: url('http://i.imgur.com/tphOPHy.png');
}
  .thread_view .threadAlerts dd {
    color: rgb(51, 97, 116);
}
  .tagList .tag {
    background: rgb(72, 72, 72);
    border: 1px solid rgb(86, 86, 86);
    color: rgb(206, 206, 206);
}
  .tagList .tag .arrow::after {
    border-right-color: rgb(72, 72, 72);
}
  .tagList .tag .arrow {
    border-right-color: rgb(86, 86, 86);
}
  .tagList .tag::after {
    border: 1px rgb(72, 72, 72);
    background: rgb(252, 252, 255);
}
  .tagList .tag:hover {
    color: black;
}

  .xenPreviewTooltip .arrow span {
    border-top: 15px solid rgb(20, 20, 20);
}
  .InlineModChecked .messageUserBlock, .InlineModChecked .messageInfo, .InlineModChecked .messageNotices, .InlineModChecked .bbCodeBlock .type, .InlineModChecked .bbCodeBlock blockquote, .InlineModChecked .attachedFiles .attachedFilesHeader, .InlineModChecked .attachedFiles .attachmentList {
    background: rgb(75, 75, 75);
}
  .InlineModChecked .section, .InlineModChecked .sectionMain, .InlineModChecked .primaryContent, .InlineModChecked .secondaryContent, .InlineModChecked .sectionFooter {
    background: rgb(75, 75, 75);
}
  .discussionListItem.InlineModChecked, .discussionListItem.InlineModChecked .posterAvatar, .discussionListItem.InlineModChecked .main, .discussionListItem.InlineModChecked .stats, .discussionListItem.InlineModChecked .lastPost, .discussionListItem.deleted.InlineModChecked, .discussionListItem.moderated.InlineModChecked {
    background: rgb(75, 75, 75);
}
  .messageSimpleList .messageSimple.InlineModChecked {
    background: rgb(75, 75, 75);
}
  .ugc a:hover, .ugc a:focus {
    color: #FFF;
    background: #000;
}
  .emCtrl:hover, .emCtrl:focus, .ugc a:hover, .ugc a:focus {
    -khtml-box-shadow: none;
    box-shadow: none;
}
  .Popup .PopupControl:hover,
  .Popup.PopupContainerControl:hover{
    color: rgb(170, 196, 44);
    background-color: rgb(25, 103, 133);
}
  .bbCodeQuote .quoteContainer .quoteExpand {
    background: transparent;
}
  ul.autoCompleteList {
    background-color: rgb(50, 50, 50);
    border: 1px solid rgb(86, 86, 86);
}
  ul.autoCompleteList li:hover,
ul.autoCompleteList li.selected
{
	background-color: rgb(80, 80, 80);
}
  .searchResult .snippet a {
    color: rgb(222, 222, 222);
}
  #StackAlerts .stackAlertContent {
    background: rgb(34, 34, 34);
      border: 1px solid rgb(72, 72, 72)
}
  .pollBlock .pollOption label:hover {
    background: rgb(71, 71, 71);
}

  .PanelScroller .panel, .PanelScrollerOff .panel {
    background: rgb(41, 41, 41) url('http://www.emudesc.com/styles/emudesc/xenforo/gradients/category-23px-light.png') repeat-x top;
}
  
  .PanelScroller .scrollContainer, .PanelScrollerOff .panel {
    background-color: rgb(77, 77, 77);
    border: 1px solid #154e77;
}
  
}
