css:
@-moz-document domain(csgolounge.com) {
body
{
    background: #161f28;
    color: #efefef;
}

a.button.destroyer
{
    padding: 0.5em;
    margin-top: 20px!important;
}

.ld-match-info-headtohead > tbody > tr > td > b, .ld-match-info-pastmatches .ld-match-info-pastmatches-won
{
    color: #1abc9c!important;
}

.ld-match-info-pastmatches .ld-match-info-pastmatches-lost
{
    color: #eb4b4b!important;
}

.matchExactTime
{
    color: #9D70FF!important;
    padding: 10px;
}

.ld-totalbets
{
    color: #FFE682
}

.eventm::before
{
    content: "Event: ";
    font-size: 14px;
	position: relative;
    top: -25px;
    right: -43px;
    color: #1abc9c
}

.eventm
{
    color: #ecf0f1!important;
    position: relative;
    right: 10px;
    top: 78px;
    font-size: 20px;
}

.seperator
{
	display: none;
    padding: 10px
}

.bestoftype
{
    display: none;
}

span.ld-btn-icon, .ld-trade-filters::before, .ld-match-filters::before
{
	-webkit-filter: invert(1);
}

.whenm > span:nth-child(2)
{
    color: #3498db!important;
    text-shadow: none!important;
    margin-left: 10px;
}

.inventoryStatisticsBox, .appID730 .marketPriced .rarity, .ld-trade-filters, .ld-match-filters, .ld-steam-extra, div.trade-description, .trade-description p
{
    background: transparent!important;
    text-shadow: none!important;
    color: #efefef!important;
}

.destroyer.auto-info
{
    background: rgba(0,0,0,0.8);
    border: none;
}

input
{
    background-color: transparent;
    border: 2px solid rgba(255,255,255,0.3);
    color: #efefef;
    padding: 4px;
    outline: 0;
    text-align: center;
}

input::-webkit-outer-spin-button,
input::-webkit-inner-spin-button
{
   -webkit-appearance: none;  
}

.ld-steam-extra
{
    border: 2px solid rgba(255,255,255,0.1);
}

.ld-steam-extra .ld-steam-info
{
    border-right: 2px solid rgba(255,255,255,0.1);
}


.ld-steam-extra::after
{
    background-image: none!important;
}

#betSizeValues
{
    font-size: 14px!important;
}

.box-shiny a, .box-shiny-alt a
{
    color: #f39c12;
}

.team
{
    border-radius: 2px;
    box-shadow: none!important;
    border: 3px solid transparent;
    background-repeat: no-repeat!important;
    padding: 3px;
    background-color: #bbb!important;
    background-position: center!important;
}

a.active > span > div.team
{
    border: 3px solid #f39c12;
}

header
{
    background: rgba(255, 255, 255,0.1);
    box-shadow: none;
}

aside
{
    background: transparent;
}

.matchheader
{
    background: transparent;
    padding: 0px;
    border-color: rgba(255, 255, 255,0.1);
}

.whenm, .eventm
{
    text-shadow: none;
    color: #1abc9c;
    margin-top: 12px;
    margin-bottom: 12px;
    margin-right: 10px;
}

.match, .winnings
{
    box-shadow: none;
    border-radius: 4px;
    width: 98%;
    background-color:  rgba(255, 255, 255,0.1);
    background-image: none!important;
}

.matchleft
{
    margin-left: auto;
    margin-right: auto;
    text-align:center
}

a, .format
{
    color: #efefef;
}

.left, .right
{
    background: transparent;
    box-shadow: none;
}

.teamtext
{
    background: transparent
}

#queue
{
    background:rgba(255, 255, 255,0.1)!important;
    box-shadow: none!important;
    border-radius: 0!important;
    padding: 10px!important;
}

#queue > a.button
{
    margin-left: 0px;
    margin-right: 0px;
}

.button, .buttonright
{
    background: rgba(0,0,0,0.5)!important;
    box-shadow: none;
    padding: 1em;
    margin: 10px 10px;
    color: #1abc9c!important;
}

.button:hover, .button:hover .changeteam:hover, .buttonright:hover
{
   background: rgba(0,0,0,0.3)!important; 
        box-shadow: none!important;
}

.tradeheader, .bpheader
{
    background: transparent;
    border-color: rgba(255,255,255,0.1);
    color: rgba(255,255,255,0.5);
}

.title
{
    color: rgba(255,255,255,0.8);
    background-image: none!important;
    padding-left: 0px!important;
}

.oitm
{
    margin-top: 0px;
}
.value
{
 	background:transparent;
    text-shadow: none;
    color: #00aeda
}

.item .rarity
{
    background: transparent;
    color: #efefef;
    font-size: 11px;
}

.clreff
{
    text-shadow: none;
    border-bottom-right-radius: 3px;
}

.StatTrak
{
    box-shadow: none;
    border-top: 2px solid #cf6a32;
    border-radius: 0px;
}

.Mil-Spec
{
    box-shadow: none;
    border-radius: 0px!important;
    border-bottom: 3px solid #4b69ff;
}
.Restricted
{
    box-shadow: none;
    border-radius: 0px!important;
    border-bottom: 3px solid #8847ff;
}
.Consumer, .Base
{    
    box-shadow: none;
    border-radius: 0px!important;
    border-bottom: 3px solid #b0c3d9;
}

.Industrial
{
    box-shadow: none;
    border-radius: 0px!important;
    border-bottom: 3px solid #5e98d9;
}
.Classified
{
	box-shadow: none;
    border-radius: 0px!important;
    border-bottom: 3px solid #d32ce6;
}
.Covert, .Extraordinary
{
    box-shadow: none;
    border-radius: 0px!important;
    border-bottom: 3px solid #eb4b4b;
}

.item
{
    background: rgba(255,255,255,0.1);
}

.winsorloses
{
	background: transparent;
}

hr
{
    border-color: rgba(255,255,255,0.1);
}

.half
{
    margin: 0.5em 1%;
}

.full
{
    margin: 0.5em 2%;    
}

.winsorloses > a span
{
    margin-top: 10px;
}

.Star
{
    box-shadow: none;
    border-top: 2px solid #8650ac;
    border-radius: 0px;
}

.name
{
    background: rgba(0,0,0,0.8);
    color: #efefef;
	border: 0px;
    padding: 1em;
}

.name a
{
    color: #00aeda!important;
    text-shadow: none!important;
}

.potwin
{
    padding: 2px;
    background: transparent;
    color: #f39c12;
}

#menu a:hover
{
    background: transparent;
    color: #e67e22
}

#menu a
{
    color: #f39c12;
    text-shadow: none;
}

#submenu nav a
{
    background: rgba(255,255,255, 0.1);
    border: 0px;
    border-radius: 3px;
    margin-bottom: 10px;
    text-align: center;
    color: #1abc9c;
}
aside a
{
    color: #1abc9c; 
}

#submenu nav a:hover
{
    background: rgba(255,255,255, 0.2);
    border-radius: 3px;
}
footer
{
    text-align:center;
}
footer > span
{
    margin-bottom: 8px;
}

aside #follow a
{
    background: url(http://puu.sh/ozyr6/cb219a415b.png) no-repeat;
    height: 50px;
}
#ftw
{
    width: 50px!important;
    background-position: -10px -10px!important;
    margin: 5px!important;
}

#ftw:hover
{
	background-position: -10px -70px!important;  
}
#ffb
{
    width: 50px!important;
    background-position: -70px -10px!important;
    margin:5px!important;
}
#ffb:hover
{
    background-position: -70px -70px!important;
}
#fvk
{
    width: 50px!important;
    background-position: -130px -10px!important;  
    margin: 5px!important;
}
#fvk:hover
{
  background-position: -130px -70px!important;    
}

#tradelist a
{
    text-shadow: none;
}
.notavailable
{
    opacity: 0.3
}

.whenm > span:first-child
{
    color: #2ecc71!important;
    margin-left: 10px;
    text-shadow: none!important;
}

#timeret
{
    margin-bottom: 10px!important;
}

#satrade, .box-shiny-alt, .box-shiny
{
    background: transparent;
    box-shadow: none;
    text-shadow: none;
}

.tab
{
    background: #2d353d;
}

.tab:hover
{
    background: #454c53;
}

tr:nth-child(odd)
{
    background: rgba(255,255,255,0.1)!important;
}

.lost
{
    text-shadow: none;
    color: #e74c3c;
}

.won
{
    text-shadow: none;
    color: #2ecc71;
}

table a, #backpack
{
    color: #efefef!important;
}

.selectbox, .selectbox:focus
{
    background: rgba(255,255,255,0.1);
    box-shadow: none;
}

nav > a > img 
{
    display: none!important;
}

#menu
{
    margin-left: 30px;
}

#logout, #login
{
    background-image: url(http://puu.sh/oC3zH/04fada3554.png);
}

#menu > a
{
    font-size: 20px;
    padding: 10px;
}

.user
{
    color: #efefef!important;
}

::-webkit-scrollbar
{
	width: 9px;
    background: rgba(0,0,0,0.1);
}

::-webkit-scrollbar-thumb
{
	border-radius: 9px;
    background: rgba(255,255,255,.1);
}
}
