css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://shizafm.com"),regexp("https?://shizafm.com/(#[a-z]*)?") {
body{
  position: relative;
}
body::before {
  background-color: #727272;
  background: 
             url("https://pp.vk.me/c630320/v630320596/2ab71/Tn8hkfuslAQ.jpg") no-repeat right center,
             #727272;
  background-size: auto 100%;
  content: ' ';
  height: 100%;
  right: 0;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: -1;
}

html, body {
	height: 99%;
}

/* Navigation
===================================================================== */

.content-nav {
  background-color: #3A9F10;
  font-size: 15px;
}

/* Reusable
===================================================================== */

.block {
  background-color: #242424;
  border-bottom: 1px solid rgba(200, 165, 165, 0.1);
  border-right: 1px solid rgba(200, 165, 165, 0.1);
  opacity: .86;
}

/* Chat
===================================================================== */

.chat-controls {
  border-top: 1px solid rgba(200, 165, 165, 0.1);
  background-color: #575757;
  height: 55px;
}

.shiza-chat-text {
  border-top: 0;
  border-bottom: 0;
  border-right: 0;
  margin-left: 87px;
  margin-top: -91px;
  position: relative;
  z-index: 5;
  min-height: 52px;
  padding: 36px 15px 13px 17px;
  font-size: 20;
  /*color: #71F639;*/
}

.avatar-border{
  border-top: 1px solid #575757;
}

.shiza-chat-text p span.bold {
  background-color: transparent !important;
  padding: inherit;
  margin: 0;
  font-weight: 600;
  font-size: 20px
}

.header-logo, .age-warning, .footer-container, .money, .infoblock, .strannik {
  display: none;
}

.shiza-chat-text p span.hashtag {
  background-color: #42B407 !important;
  border-radius: 0px;
  padding: 5px 7px !important;
  color: #1B1B1B;
  margin: 0 3px;
}
.player-controls .icon{
	background: url('data:image/png;base64,') no-repeat 0;
	width: 19px;
	height: 19px;
	display: block;
}
.track-list .plays{
	background: url('data:image/png;base64,') no-repeat 14px;
}

.jp-volume-bar-value {
	background-color: #55F40E;
	border-radius: 3px;
	width: 0px;
	height: 5px;
}

.slimScrollBar {
    background: #55F40E !important;
}

#broad-btn span {
  background-color: transparent !important;
  border-radius: 0px;
  color: #000000 !important;
}

#broad-btn {
  background-color: #42B407 !important;
}
}
