/*
LL25 MENU (FF & PM) - Bigger & compatible w Palemoon

Based on ELIAN â–º MENU http://userstyles.org/styles/104409/

Look for "LL25" for all my changes

Elian firefox version of this style had mainly 2 problems in palemoon :
- the Pale Moon app menu (the menu pallete popping after u click the Pale Moon big button)
- The bookmarks drop-down menu

*/



@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/*  MENUBARS (NOT MAIN WINDOW) */
menubar {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    border-bottom: 0px solid rgb(20,20,20) !important;
}/*  */
/*  MENUBAR ITEMS - ACTIVE WINDOW (NOT MAIN WINDOW) */
menubar > menu {
    color: white !important;
}/*  */
menubar > menu[_moz-menuactive="true"]:not([disabled="true"]) {
    color: rgb(51,159,255) !important;
}
/*  MENUBAR ITEMS - INACTIVE WINDOW (NOT MAIN WINDOW) */
menubar > menu:-moz-window-inactive {
    color: rgb(128,128,128) !important;
}/*  */





/*  BACKGROUND MENU */
menupopup:not([id="BMB_bookmarksPopup777"]), /* LL25 remarked - now compatible w Pale Moon */
#BMB_bookmarksPopup menupopup[placespopup=true] > hbox
{
    -moz-appearance: none !important;
    border-radius: 4px !important;
    font-weight: normal !important;
    border: 1px solid rgb(22,25,28) !important;
    box-shadow:
        0 1px 0 rgba(162,184,204,0.25) inset,
        0 0 4px hsla(0,0%,0%,0.95) !important;
    background-color: transparent !important;
    background-image:
            linear-gradient(to bottom,
                rgb(37,46,54),
                rgb(36,40,45)
                ) !important;
}
menupopup:not([id="BMB_bookmarksPopup777"]) /* LL25 remarked - now compatible w Pale Moon */
{
    margin-top: -0px ;
    padding-top: 5px ;
    padding-bottom: 5px ;
}
menubar > menu > menupopup {
    margin-top: 0px !important;
}

#BMB_bookmarksPopup menupopup[placespopup=true] {
    background: transparent !important;
    box-shadow: none !important;
    border: 0px !important;
}
/*  ----  */

/*  BACKGROUND PANEL  */
.panel-arrow[side="top"],
.panel-arrow[side="bottom"] { list-style-image: url(
            data:image/svg+xml;base64,
            ) !important; position: relative; }
.panel-arrow[side="left"],
.panel-arrow[side="right"] { list-style-image: url(
            data:image/svg+xml;base64,
            ) !important; position: relative; }

.panel-arrowcontent {
    border: 1px solid rgb(22,25,28) !important;
    border-radius: 4px !important;
    box-shadow:
        0 1px 0 rgba(162,184,204,0.25) inset,
        0 0 4px hsla(0,0%,0%,0.95) !important;
    background-color: transparent !important;
    background-image:
            linear-gradient(to bottom,
                rgb(37,46,54),
                rgb(36,40,45)
                ) !important;


    color: rgb(204,204,204) !important;

    /* 2016 =================================== LL25 */
    /* 
    changing the color b/c the bright color affected 
    https://addons.mozilla.org/en-US/firefox/addon/youtube-control-center/
    /*
    /* 2016 =================================== LL25 */
    /* color:#933!important; /* */


    margin: 3px !important;
}
/*  --  */
/*  BACKGROUND SUB PANEL  */
.panel-subviews {
    padding: 4px;
    -moz-margin-start: 38px;
    background-clip: padding-box;
    background-color: transparent !important;
    background-image:
            linear-gradient(to bottom,
                rgb(37,46,54),
                rgb(36,40,45)
                ) !important;
    border-left: 1px solid hsla(210,4%,10%,.3);
    box-shadow:
        -5px 0px 5px -2px rgba(0,0,0,0.5)
        !important;
}
/*  --  */



/*  MENU ITEMS  */
menu,
menuitem,
.splitmenu-menuitem {
    -moz-appearance: none !important;
    /* color: rgb(204,204,204) !important; /**/
    color:#e5e5e5!important; /* LL25 - this is the line that affects most menus LL25 */
}
menu[disabled="true"],
menuitem[disabled="true"],
.splitmenu-menuitem[disabled="true"],
menu[_moz-menuactive="true"][disabled="true"],
menuitem[_moz-menuactive="true"][disabled="true"],
.splitmenu-menuitem[_moz-menuactive="true"][disabled="true"],
toolbarbutton[disabled="true"] {
    color: rgb(120,120,120) !important;
}
menupopup menuitem[_moz-menuactive="true"][disabled="true"] {
    background-color: rgb(20,20,20) !important;
}
menu[_moz-menuactive="true"],
menuitem[_moz-menuactive="true"],
.splitmenu-menuitem[_moz-menuactive="true"] {
    background-color: #ff6!important; 
    color: rgb(51,159,255) !important; /* maybe for default theme */
    text-shadow:none!important; /* LL25 2017-06-21 menubar */
}


menuitem[default="true"] {
    font-weight: bold !important;
}
menuitem[label="(Empty)"],
menuitem[label="(Empty)"][_moz-menuactive="true"] {
    -moz-appearance: none !important;
    color: rgb(191,117,43) !important;
    background-color: transparent !important;
    min-height: 20px !important;
    max-height: 20px !important;
}
/*  ----  */

/*  --  *
#PanelUI-history {display: -moz-box !important;}/*  --  */



/*  PANEL ITEMS  */
panelview .toolbarbutton-1:not(:-moz-any([disabled],[open],:active)),
#PanelUI-footer toolbarbutton:not(:-moz-any([disabled],[open],:active)),
toolbarbutton.subviewbutton:not(:-moz-any([disabled],[open],:active)),
menu.subviewbutton:not(:-moz-any([disabled],:active)),
menuitem.subviewbutton:not(:-moz-any([disabled],:active)),
.share-provider-button:not(:-moz-any([disabled],[open],:active)),
.widget-overflow-list .toolbarbutton-1:not(:-moz-any([disabled],[open],:active)),
.toolbaritem-combined-buttons:-moz-any(:not([cui-areatype="toolbar"]), [overflowedItem=true]) > toolbarbutton:not(:-moz-any([disabled],[open],:active))
{
    -moz-appearance: none !important;

    /* color: rgb(204,204,204) !important; /**/
    color:#e5e5e5!important; /* LL25 */



}
panelview .toolbarbutton-1:not(:-moz-any([disabled],[open],:active)):hover,
toolbarbutton.subviewbutton:not(:-moz-any([disabled],[open],:active)):hover,
menu.subviewbutton:not(:-moz-any([disabled],:active))[_moz-menuactive],
menuitem.subviewbutton:not(:-moz-any([disabled],:active))[_moz-menuactive],
.share-provider-button:not(:-moz-any([disabled],[open],:active)):hover,
.widget-overflow-list .toolbarbutton-1:not(:-moz-any([disabled],[open],:active)):hover,
.toolbaritem-combined-buttons:-moz-any(:not([cui-areatype="toolbar"]), [overflowedItem=true]) > toolbarbutton:not(:-moz-any([disabled],[open],:active)):hover,

  .subviewbutton:not([disabled])[_moz-menuactive],
  menuitem.panel-subview-footer:not(:-moz-any([disabled],:active))[_moz-menuactive],
  .subviewbutton.panel-subview-footer:not(:-moz-any([disabled],[open],:active)):hover,
  .subviewbutton.panel-subview-footer:not([disabled]):-moz-any([open],:hover:active),
  .subviewbutton:not(:-moz-any([disabled],:active))[_moz-menuactive] > .menu-accel-container,
  .PanelUI-subView .subviewbutton[shortcut]:not(:-moz-any([disabled],[open],:active)):hover::after,
  #BMB_bookmarksPopup .panel-subview-footer:not([disabled])[_moz-menuactive] > .menu-text
{
    background-color: rgb(20,20,20) !important;
    color: rgb(51,159,255) !important;
    border-color: transparent !important;
}
/*  --  */



/*  PANEL FOOTER  */
#PanelUI-footer {

    /* color: rgb(204,204,204) !important; /**/
    color:#e5e5e5!important; /* LL25 */

    background-color: rgba(24,25,26,0.3) !important;
}
#PanelUI-footer > * {
    border-top: 1px solid rgba(24,25,26,0.5) !important;
}
#PanelUI-fxa-status:not([disabled]):hover,
#PanelUI-footer-inner > toolbarbutton:not([disabled]):not([id="PanelUI-quit"]):hover {
    background-color: rgb(20,20,20) !important;
    color: rgb(51,159,255) !important;
    border: 1px solid transparent !important;
    outline: 1px solid transparent !important;
}
#customization-panelHolder #PanelUI-customize {
    color: white !important;
    background-color: rgb(116,191,67);
}
#customization-panelHolder #PanelUI-customize:hover,
#customization-panelHolder #PanelUI-customize:hover:active {
    color: rgb(116,220,67) !important;
    background-color: rgb(105,173,61);
}
/*  --  */





/* ..... internal content .... */
.menu-accel,
.menu-iconic-accel,
.menu-text,
.menu-iconic-text {
    -moz-appearance: none !important;
    margin: 0px 0px 0px 0px !important;
    padding: 2px 0px 2px 0px !important;
    font-size: 14px !important; /* LL25 */
    /* font-weight: 600 !important; /* LL25 */
    color: inherit !important;
}

/* LL25 -make bookmarks menu wider */
.menuitem-iconic.bookmark-item {
   font-size: 16px !important; /* this line effects the item width NOT the font-size which was changed in the lines above */
}



.menu-text {
    -moz-padding-start: 19px !important;
}
.menu-text,
.menu-iconic-text {
    -moz-margin-start: 2px !important;
    -moz-padding-end: 2px !important;
}
.menu-description {
    -moz-appearance: none !important;
    font-style: italic;
    color: GrayText !important;
    -moz-margin-start: 1ex !important;
}
.menu-accel,
.menu-iconic-accel {
    color: GrayText !important;
    background-color: transparent !important;
    -moz-margin-start: 0.74em !important;
    -moz-margin-end: 1.35em !important;
}
.menu-iconic-left {
    -moz-appearance: none !important;
    min-width: 16px !important;
    -moz-margin-start: 3px !important;
}
.menu-iconic-icon {
    -moz-appearance: none !important;
    width: 16px !important;
    height: 16px !important;
}
menu.menu-iconic > .menu-iconic-left,
menuitem.menuitem-iconic > .menu-iconic-left,
.splitmenu-menuitem[iconic="true"] > .menu-iconic-left {
    -moz-appearance: none !important;
    padding: 2px 0px 2px 0px !important;
}
/*  --  */













/*  SEPARATORS  */
menuseparator {
    -moz-appearance: none !important;
    padding: 0px 1px 0px 1px;
    margin: 2px 0px 2px 0px;
    border-top: 0px solid !important;
    border-bottom: 0px solid !important;
    height: 2px !important;
    background: linear-gradient(to right,
                       rgba(9,13,17,0) 0%,rgba(9,13,17,1) 20%,
                       rgba(9,13,17,1) 80%,rgba(9,13,17,0) 100%) ,
                       linear-gradient(to right,
                       rgba(58,70,80,0) 0%,rgba(58,70,80,1) 20%,
                       rgba(58,70,80,1) 80%,rgba(58,70,80,0) 100%)
                       !important;
    background-repeat: no-repeat, no-repeat !important;
    background-size: 100% 1px , 100% 1px !important;
    background-position: top center, bottom center !important;
    background-color: transparent !important;
}
.PanelUI-subView menuseparator,
.PanelUI-subView toolbarseparator,
.cui-widget-panelview menuseparator {
    margin: 2px 0 !important;
    padding: 0px !important;
    border: 0px solid !important;
    height: 2px !important;
    background: linear-gradient(to right,
                       rgba(9,13,17,0) 0%,rgba(9,13,17,1) 20%,
                       rgba(9,13,17,1) 80%,rgba(9,13,17,0) 100%) ,
                       linear-gradient(to right,
                       rgba(58,70,80,0) 0%,rgba(58,70,80,1) 20%,
                       rgba(58,70,80,1) 80%,rgba(58,70,80,0) 100%)
                       !important;
    background-repeat: no-repeat, no-repeat !important;
    background-size: 100% 1px , 100% 1px !important;
    background-position: top center, bottom center !important;
    background-color: transparent !important;
}

