/*  CONTEXT NAVIGATION ICONS  */
#context-navigation {
    background-color: transparent !important;
    margin-bottom: 2px !important;
}
#context-navigation > .menuitem-iconic > .menu-iconic-left > .menu-iconic-icon {
    margin: 1px 7px 3px 7px !important;
}
#context-navigation > .menuitem-iconic > .menu-iconic-left {
    -moz-appearance: none !important;
}
#context-back ,
#context-back[_moz-menuactive=true] ,
#context-back[disabled=true] ,
#context-forward ,
#context-forward[_moz-menuactive=true] ,
#context-forward[disabled=true] ,
#context-reload ,
#context-reload[_moz-menuactive=true] ,
#context-reload[disabled=true] ,
#context-stop ,
#context-stop[_moz-menuactive=true] ,
#context-stop[disabled=true] ,
#context-bookmarkpage ,
#context-bookmarkpage[_moz-menuactive=true] ,
#context-bookmarkpage[disabled=true] {
    list-style-image: url(
        data:image/png;base64,
    ) !important;}
#context-back {
    -moz-image-region: rect(0px,16px,16px,0px) !important;}
#context-back[_moz-menuactive=true] {
    -moz-image-region: rect(0px,96px,16px,80px) !important;}
#context-back[disabled=true] {
    -moz-image-region: rect(0px,176px,16px,160px) !important;}
#context-forward {
    -moz-image-region: rect(0px,32px,16px,16px) !important;}
#context-forward[_moz-menuactive=true] {
    -moz-image-region: rect(0px,112px,16px,96px) !important;}
#context-forward[disabled=true] {
    -moz-image-region: rect(0px,192px,16px,176px) !important;}
#context-reload {
    -moz-image-region: rect(0px,48px,16px,32px) !important;}
#context-reload[_moz-menuactive=true] {
    -moz-image-region: rect(0px,128px,16px,112px) !important;}
#context-reload[disabled=true] {
    -moz-image-region: rect(0px,208px,16px,192px) !important;}
#context-stop {
    -moz-image-region: rect(0px,64px,16px,48px) !important;}
#context-stop[_moz-menuactive=true] {
