css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("legiaodosherois.uol.com.br") {
  #barrauol,
  #header-top,
  #sidebar,
  #footer-end,
  #footer-content,
  .post-meta.clearfix.hidden-xs.hidden-sm,
  #breadcrumbs,
  .lh-suggested-video,
  .alert.alert-warning.comments-warning,
  .addthis_default_style.single-like-toolbox.clearfix,
  .addthis_jumbo_share,
  .userpro-centered,
  .title.singletitle,
  .post-meta.clearfix,
  .btn.btn-xs.btn-lh-secondary,
  .img-circle.profile-pic,
  .col-md-7,
  .profile-pic-link,
  #banner-300x250-area,
  #banner-970x250-area,
  .addthis_lista,
  .list-bottom-ads,
  .userpro,
  #bg-space-holder,
  #footer-content,
  .frieza-image,
  #footer-end,
  .profile-card,
  .jumbo-divider,
  .social-sharing-jumbo,
  .tm-ads-rodape-wrapper,
  .premio-influenciador,
  .fve-max-width-wrapper,
  .youtube-feature,
  .yt-feature-list
  {display: none!important}
.col-md-8{width: 1000px}
  #content{background-image: url(http://universovisual.net/inelectra/navidad/images/stars2.png); background-attachment: fixed; background-color: #1b2e43;}
  .slide .description{margin-left: -15px; padding-left: 0px; padding-right: 0px; max-width: 970px !important}
  .col-md-8{width: 1000px; padding-right: 14px; border-right: 0px !important}
  .title{width: 971px !important}
  .description{margin-left: -70px; margin-right: 0px; padding-left: 0px; padding-right: 0px !important}
  .recent-posts .entry h2.title{width: 940px !important}
  .subtitle-info{width: 940px; margin-left: 0px; margin-right: 0px; margin-left: 0px!important}
  .title-content{padding-left: 0px; padding-right: 0px; padding-top: 5px; padding-bottom: 5px; text-align: center; margin-left: -10px;  margin-right: auto; width: 95%; overflow: inherit !important} 
  .entry-content p{ font-size:15px; padding-bottom: 15px !important}
  .recent-posts{ font-size:13px; margin-top: 10px; padding: 0px !important}
  .entry.post.clearfix{margin-top: -10px;padding-left: 15px; padding-right: 15px; padding-top: 0px; padding-bottom: 0px!important}
  .list-description{font-size:14px; margin-top: 20px !important}
  .col-md-offset-1{width: 960px; padding-left: 15px !important}
  #disqus_thread{padding-left: 10px; padding-right: 10px; border-radius: 3px !important}
  div.title-holder img.cover{padding-left: 10px; padding-top: 10px; width: 99%}
  #FOOTER,footer{height: 0px; margin-top: 0px; padding-bottom: 0px}
  .navbar{background: rgba(34,34,34,.98); border-top: 1px solid #363636; border-bottom: 2px solid #101010;}
  #featured{margin-top: 0px; padding: 0px; margin-bottom: -20px; height:300px !important}
  .entry,#comment-wrap {border-radius: 3px; box-shadow: 0px 2px 26px rgb(0, 9, 12)  !important}
  .list-image{background: #000}
  h4.title{background: #e5e5e5}
  .cover-holder{margin-right: 1px; margin-left:1px; margin-top: 15px}
  .social-info{margin-right: 0px; margin-left: 0px; padding-right: 10px; padding-left: 0px}
  elemento{margin-left: 900px}
  body{background-image: url(http://universovisual.net/inelectra/navidad/images/stars2.png); background-attachment: fixed; background-color: #1b2e43;}
}
