css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.rinkworks.com/adventure/smash.cgi) {

table [bgcolor='#ffe4dd']
   { /* ACTION DESCRIPTION TEXT */ 
   background-color: #EED2EE !important;
   -moz-border-radius: 10px !important;
   border-width: thin;
   border-color: #000000;
   border-style: solid;
   }

table [bgcolor='#ccccff']
   { /* AREA DESCRIPTION TEXT */
   background-color: #87CEEB !important;
   -moz-border-radius: 10px !important;
   border-width: thin;
   border-color: #000000;
   border-style: solid;
   }

td [bgcolor='#e8ffe8']
   { /* INVENTORY */
   background-color: #9AFF9A !important;
   -moz-border-radius: 10px !important;
   border-width: thin;
   border-color: #000000;
   border-style: solid;
   }

table[cellpadding='0'] td[valign='top'] table
   { /* COLUMN OF OPTIONS */
   background-color: #CCCCFF !important;
   padding: 3px;
   -moz-border-radius: 10px !important;
   border-width: thin;
   border-color: #000000;
   border-style: solid;
   }

td [bgcolor='#ffffdd']
   { /* STATUS BOX */
   background-color: #CCACFF !important;
   padding: 3px;
   -moz-border-radius: 10px ! important;
   border-width: thin;
   border-color: #000000;
   border-style: solid;
   }

}
