css:
@-moz-document domain("vimeo.com") {
html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form
{
 background: #000 !important;
}

#main
{
background: #000 !important; 
}

#site_header nav > ul > li > ul, #site_header .submenus > ul
{
background: #000 !important;
padding: 10px !important;
}

nav strong
{
color:#fff !important;
}

#site_header
{
 background: #000 !important;
 border:none !important;
}

#logo
{
 background: #5590A5;
 border-radius: 0em 0em 1em 1em;
 padding: 10px 0;
 margin: -11px 0;
}

#logo:hover
{
 background: #4BF !important;
 box-shadow: 0 0 10px #5590A5 !important;
}
select, button
{
 background: black !important;
}

#tools, .comment
{
 background-color: black !important;
}

.topnav_desktop {
    position: relative;
    z-index: 999;
    font-size: 14px;
    border-bottom: 1px solid #000;
    background-color: #000;
}
*, *::after, *::before {
    box-sizing: border-box;
}
  
#tabs a.selected,
#tabs a.selected:hover
{
 background: #5590A5 !important;
 border: #5590A5 !important;
 color: black !important;
}

#tabs a
{
 border: none!important
}

.iris_comment-replies .iris_comment-content {
    margin-bottom: 1.875rem;
    padding: .9375rem;
      background-color: #010305 !important;
    color: #808080 !important;
}
.iris_btn-truncate, .iris_btn-utility {
    padding: .625rem;
    font-size: 14px;
    color: #A1B6C5 !important;
    background-color: #000 !important;
    box-shadow: inset 0 0 0 1px #D0D8DB;
}

.iris_link-header {
    font-weight: 700;
    color: #fff !important;
}

.boxed, #tabs
{
 background: #000 !important;
 border: none !important
}

#tabs a:hover.selected
{
 box-shadow: 0 0 10px #5590A5;
}

#content a:hover
{
color: #4BF !important;
}

#inbox_ad,
#channel_header_back,
#channel_header
{
 display:none;
}

#comments article,
.tags,
#tools
{
 border: #000 !important;
 border-top: 1px solid #2B2B2B !important;
}

#search_form input[type="text"]:focus,
#search_form input[type="text"]:focus
{
box-shadow: 0 0 10px #5590A5;
} 
  
  
.iris_fader::before {
    top: 0;
    left: 0;
}
.iris_fader::after, .iris_fader::before {
    display: block;
    position: relative;
    z-index: 5;
    width: 100%;
    height: 7.5rem;
    margin-top: -3.125rem;
    background-color: transparent;
    content: "";
    background-image: -webkit-linear-gradient(top,rgba(247,247,247,0) 0,#F9FAFB 34%,#F9FAFB 50%);
    background-image: linear-gradient(to bottom,rgba(247,247,247,0) 0,#000 34%,#000 50%);
}

.footer_v2_accordion__check:checked,body {
	-webkit-tap-highlight-color:transparent
}
#wrap_content {
	max-width:1000px;
	margin:auto
}
.body_ribbon {
	width:100%;
	height:5px;
	position:relative;
	z-index:3
}
.container_title_logo_trigger {
	display:none
}
.outer_wrap {
	background-color:#000000;
}
  
.iris_grid_fp1260 .iris_grid-wrapper--sidebar {
    width: 29.34%;
    float: right;
    margin-right: 0;
}
.clip-contextual-clips {
    position: relative;
    min-height: 0;
    padding-top: 1.5625rem;
    padding-bottom: 1.5625rem;
    border-left: 1px solid #D0D8DB;
    background-color: #000000;
}
*, *::after, *::before {
    box-sizing: border-box;

}
.clip_details {
    padding: .9375rem 0;
    background-color: #000000;
}
.iris_divider-top--m {
    margin-top: 1.875rem;
}
.iris_divider--dark {
    border-color: #D0D8DB;
}
.iris_divider-top {
    border-top: 1px solid;
}
.iris_divider-bot {
    border-bottom: 1px solid;
}
.iris_divider-bot--m, .iris_divider-bot--pm {
    margin-bottom: 1.875rem;
}
*, *::after, *::before {
    box-sizing: border-box;
}

.hem {
    clear: both;
    background: #000000;
    overflow: hidden;
    margin-bottom: 20px;
    padding: 10px 20px;
}  
  
select, 
button, 
#tools, 
.comment
{
 background: none !important;
}

#info #page_header h1
{
 color: #5590A5 !important;
}

#clip
{
 overflow:visible !important;
}

/* */
#clip:hover > #info
{
opacity: 0.5!important;
}
/* */

#info .description.collapsed::after
{
background:none !important;
}

iframe
{
 display:none;
}

#content a
{
 color: #8DA1BB !important;
}

#tabs a.selected,
#content .btn,
#content a,
#logo,
.boxed
{
  -webkit-transition: all 0.3s ease-in-out; 
     -moz-transition: all 0.3s ease-in-out; 
       -o-transition: all 0.3s ease-in-out; 
        -ms-transition: all 0.3s ease-in-out;
}

#tabs a.selected
{
background: #345!important;
border-radius: 5px;
color:#9CA4B1 !important;
 
}

.col_small .block
{
overflow: visible !important;
}

#content a:hover.btn
{
 color: black !important;
 box-shadow: 0 0 10px #5590A5;
}

#tabs a:hover.selected
{
background: #4BF!important;
color: black !important;
}

#info
{
 background: none !important;
 border: none !important;
}

textarea, pre, input, code

{
 background: #333333 !important;
}

#comments #show_all_comments::after
{
 background-image: none !important;
}

#comments #show_all_comments::before
{
 background-image: none !important;
}

  
  .footer_v2__sitemap {
    display: flex;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
    -ms-flex-pack: justify;
    justify-content: space-between;
    padding: 25px 30px;
    background-color: #000000;
    border-top: 1px solid #000000;
    border-bottom: 1px solid #000000;
    box-sizing: border-box;
}
  
  
  .footer_v2__tip {
    position: relative;
    padding: 1.25rem 1.25rem 1.4375rem;
    margin-bottom: .625rem;
    width: 340px;
    min-height: 147px;
    background-color: #000000;
    box-shadow: inset 0 0 0 1px #E3E8E9;
    -webkit-box-flex: 0;
    -webkit-flex-grow: 0;
    -ms-flex-positive: 0;
    flex-grow: 0;
    -webkit-flex-shrink: 1;
    -ms-flex-negative: 1;
    flex-shrink: 1;
    box-sizing: border-box;
}
  
  .footer_v2__auxiliary {
    font-size: .75rem;
    padding: 1.25rem 1.875rem 1.125rem;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
    -ms-flex-pack: justify;
    justify-content: space-between;
    box-sizing: border-box;
    background-color: #000000;
}
  
#comments #show_all_comments .show_all_link a
{
 background-color: #1F1F1F !important;
}
}
