css:
@-moz-document url-prefix("http://kanji.koohii.com/review") {
@font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3aCWcynf_cDxXwCLxiixG1c.ttf) format('truetype');
}


* {   
    font-family: 'Open Sans', sans-serif !important;
    transition: color 0.2s, background 0.2s;
}

body {
    background: #303030 !important;
}

#uiFcMain, .uiFcStBox {
    background: #424242 !important;
    border-radius: 3px !important;
    color: #fff;
}

.JsPass.stack.p {
    background: url('data:image/png;base64,') no-repeat center left !important;
}

.JsFail.stack.f {
    background: url('data:image/png;base64,') no-repeat center left !important;
}

.stack, h3 em, #uiFcEnd a {
    color: rgba(255, 255, 255, 0.7) !important;
}

h3, #keyword a, .fcData-kanji {
    color: #fff !important;
}

.fcData-kanji {
    font-family: "Hiragino Mincho Pro", "ヒラギノ明朝 Pro W3","ＭＳ 明朝", "ＭＳ Ｐ明朝", serif !important;
}

#keyword a {
    line-height: 35px !important;
    margin-left: 10px;
}

#uiFcMenu {
    background: transparent !important;
}

#uiFcMenu div {
    background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABgAAAAYCAQAAABKfvVzAAAAN0lEQVQ4y2NgGKbgf9D/1/9f/fcnXsPr/yDwEpscE3YdSCRRNvj/f/n/xX/f0VAaDaVhGEpUBQDnbkP8bmeeCwAAAABJRU5ErkJggg==') no-repeat center center !important;
    width: 24px !important;
    height: 24px !important;
}

#uiFcEnd a {
    border-bottom: none;
}

#uiFcEnd a:hover {
    border-bottom: none;
    margin-top: 0;
    color: #fff !important;
}

.uiFcOptBtn {
    background: #424242 !important;
    color: #fff !important;
}

.uiFcOptBtn * {
    color: #fff !important;
}

.uiFcOptBtn:hover, .uiFcOptBtn:active {
    background: #505050 !important;
}

#review-progress {
    background: transparent !important;
    border-radius: 3px;
}

#review-progress div {
    border: none !important;
    border-radius: 3px !important;
}

.uiProgressBar div, .uiProgressBar span {
    background: #616161 !important;
}

.uiProgressBar span.g {
    background: #4CAF50 !important;
    border-radius: 3px !important;
}

#uiFcBtnAF {
    background: #03A9F4 !important;
}

.uiIBtnDefault {
    background: #43A047 !important;
}

.uiIBtnRed {
    background: #E53935 !important;
}

#uiFcBtnAE {
    background: #0097A7 !important;
}

#keyword {
    width: auto !important;
}
}
