css:
@-moz-document url-prefix("http://socialani.me/@") {
button.fol.nofollow {
    position: relative;
    padding-top: 8px!important;
    padding-bottom: 17px!important;
    color: rgba(255,255,255,1);
    text-decoration: none;
    font-weight: 700;
    font-size: 3em;
    display: block;
    padding: 4px;
    -webkit-border-radius: 8px;
    -moz-border-radius: 8px;
    border-radius: 8px;
    -webkit-box-shadow: 0px 9px 0px rgb(218, 106, 0), 0px 9px 25px rgba(0,0,0,.7);
    -moz-box-shadow: 0px 9px 0px rgb(218, 106, 0), 0px 9px 25px rgba(0,0,0,.7);
    box-shadow: 0px 9px 0px rgb(218, 106, 0), 0px 9px 25px rgba(0,0,0,.7);
    /* margin: 100px auto; */
    width: auto;
    text-align: center;
    -webkit-transition: all .1s ease;
    -moz-transition: all .1s ease;
    -ms-transition: all .1s ease;
    -o-transition: all .1s ease;
    transition: all .1s ease;
}
button.fol.nofollow:active{
    -webkit-box-shadow: 0px 3px 0px rgb(218, 106, 0), 0px 3px 6px rgba(0,0,0,.9);
    -moz-box-shadow: 0px 3px 0px rgb(218, 106, 0), 0px 3px 6px rgba(0,0,0,.9);
    box-shadow: 0px 3px 0px rgb(218, 106, 0), 0px 3px 6px rgba(0,0,0,.9);
    position: relative;
    top: 6px;
}

