css:
@-moz-document domain("asana.com") {
body {
    font-family: tahoma, "proxima-nova","Helvetica Neue",Arial,sans-serif;
}

.grid-uncompact .task-row .grid-cell, .grid-uncompact .grid_pseudorow_no_prioritized .grid-cell, .autogrow-textarea {
	font-size: 13px;   
}

.item-list-groups {
    direction: rtl;
}

.grid-uncompact #grid {
    direction: rtl!important;
}

.grid-uncompact .grid_cell_drag_handle {
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 3px;
    border-right: 1px;
    border-left: 0;
    border-top-left-radius: 0;
    border-top-right-radius: 3px;
}

.grid-uncompact .itemRow-dragHandle {
    padding-left: 0!important;
    padding-right: 5px;
}

.taskCommentsView-autogrowHypertext .placeholder-text, .taskCommentsView-placeholder {
    padding: 5px 10px 0 10px;
}

.grid-uncompact .assign-from-grid-link, .grid-uncompact .task-row-assignee {
    margin-left: 5px;
    margin-right: 5px;
}

.grid-uncompact .grid_cell_show_details {
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 3px;
    border-top-right-radius: 0;
    border-top-left-radius: 3px;
    border-right: 0px;
}

.grid-uncompact .grid_cell_show_details {
    padding-right: 0;
    padding-left: 20px;
}

.grid-uncompact .task-row:hover .grid_cell_drag_handle, #Flags.enable_no_flexbox_in_grid .grid-uncompact .grid_pseudorow_no_prioritized:hover .grid_cell_drag_handle, #Flags.enable_no_flexbox_in_grid .grid-uncompact .bar-row:hover .grid_cell_drag_handle {
    border-left: 0;
    border-right: 0;
}

.grid-uncompact .task-row:hover .grid_cell_show_details, #Flags.enable_no_flexbox_in_grid .grid-uncompact .grid_pseudorow_no_prioritized:hover .grid_cell_show_details, #Flags.enable_no_flexbox_in_grid .grid-uncompact .bar-row:hover .grid_cell_show_details {
    border-right: 0;
    border-left: 0;
}

.itemRowView-detailsButton .itemRowView-detailsButton-seeMore, .itemRowView-detailsButton .itemRowView-detailsButton-seeMore .svgIcon {
    transform: rotate(90deg);
}


.detailsPaneProjectTokenizerView {
    margin-right: 15px;
    direction: rtl;
}

.detailsPaneProjectTokenizerView-token .projectToken-subtaskArrow {
    transform: rotate(180deg);
}

.detailsPaneProjectTokenizerView-sectionLabel {
    margin-right: 0;
    margin-left: 5px;
}

.detailsPaneProjectTokenizerView-token .token_x {
    margin-left: 0;
    margin-right: 30px;
}

.details-pane-title .header-name {
    direction: rtl;
    padding-left: 5px;
    padding-right: 50px;
}

.detailsTitle-closedAsDupe, .detailsTitle-convertedToProject, .detailsTitle-checkmarkButton {
    left: auto;
    right: 5px;
}

.taskDetailsView-description {
    direction: rtl;
    padding-right: 40px;
}

.taskCommentsView-composer.has-nonEmptyDraft:not(.is-focused) .taskCommentsView-hypertextInput {
    direction: rtl;
}

.taskCommentsView-autogrowHypertext .taskCommentsView-hypertextInput {
    direction: rtl;
}

.blockStoryView {
    direction: rtl;
}

.blockStoryView-avatar {
    margin-right: 0;
    margin-left: 10px;
}

.blockStoryView-heartButton {
    margin-left: 0;
    margin-right: 10px;
}

.feedStoryView-actionButton {
    right: auto;
    left: 0;
}

.gridCell-compactRowContainer {
    direction: rtl;
}

.gridCell-compactRowContainer .assign-from-grid-link, .gridCell-compactRowContainer .task-row-assignee {
   margin-left: 0;
    margin-right: 5px;
}

.ancestry-container {
    direction: rtl;
}

.ancestry-container .ancestor-projects .token {
    margin-right: 0;
    margin-left: 10px;
}

.taskDetailsView-ancestorArrow {
    transform: rotate(180deg);
}

.team-feed-conversation>.message-view {
    direction: rtl;
}

.messageView-by {
    margin-left: 0;
    margin-right: 5px;
}

.message-view {
    margin-right: 0;
    padding-right: 0;
	margin-left: -30px;
    padding-left: 30px;
}

.message-view .subject-link {
    margin-right: 0;
    margin-left: 10px;
}

.autogrow-textarea {
    direction: rtl;
}

.message-composer-form .composer-input-wrap .field-description.subject {
   direction: rtl; 
}

.task-row-overlay {
    margin: 0 auto 0 0;
}

.inboxThreadView {
    padding-right: 30px;
    padding-left: 0;
}

.inbox .inbox-thread-wrapper .inbox-archive-parent {
    flex-direction: row-reverse;
}

.inboxThreadView-typeHeader {
   direction: rtl; 
}

.inboxThreadView-type {
    margin-left: 0;
    margin-right: 5px;
}

.inboxThreadView-archiveButton, .inboxThreadView-unarchiveButton {
    margin-left: 0;
    margin-right: 5px;
}

.notification-header .notification-title {
	direction: rtl;   
}

.itemListInboxThreadView-notifications:before {
    right: 0;
    left: 61px;
}

.inbox .inbox-thread-wrapper .story.selectable {
	direction: rtl;    
}

.referenced-task-row-view .referenced-task-row-view-name {
    line-height: 18px;
}

.itemListInboxThreadView-creators {
    direction: rtl;
}

.inbox .inbox-thread-wrapper .project-footer.itemListInboxThreadView-footer {
    margin-left: 65px;
    margin-right: 0;
}


}
