css:
@document url-prefix("https://docs.google.com/forms/"){

 
div.freebirdFormviewerViewFormContentWrapper{
 background-color:#212121 !important;
}
/* form edit page's response section's summary section background color fix & response recorded page color fix */
.freebirdLightBackground{
background-color:#212121 !important; 
/* form editing page, responses section text color fix*/
color:#757575 !important;
}

 /*Make form filling box's background black, and text grey*/
div.freebirdFormviewerViewFormContent{
 background-color:#424242 !important; 
 color: #DDD !important; 

}
 /*Make Question's title grey*/
div.freebirdFormviewerViewItemsItemItemTitle{
 color: #DDD !important;
}

/* Make Header black*/
.freebirdHeaderMast{
 background-color:#212121 !important;
}

/*Accent banner */
.freebirdAccentBackground{
background-color:#BDBDBD !important; 
}
/*Make input field underline grey*/
.quantumWizTextinputPapertextareaUnderline{
background-color:#585858 !important; 
}
/* selected input field underline color*/
.freebirdThemedInput .exportFocusUnderline{
background-color:#585858 !important; 
}

/*Make input text grey*/
.quantumWizTextinputPapertextareaInput{
 color: #DDD !important;
}
/*Make "Your answer" grey*/
.quantumWizTextinputPapertextareaPlaceholder{
  color: #7c7c7c !important;
}
/* Form response page's stuffs*/
/* Form response box blackening*/
.freebirdFormviewerViewResponseConfirmContentContainer{
background-color:#424242 !important;
}
.freebirdFormviewerViewResponsePageTitle{
color: #BDBDBD !important;
}
.freebirdFormviewerViewResponseConfirmationMessage{
color: #BDBDBD !important;
}
.freebirdFormviewerViewResponseBottomLink{
 color:#039BE5 !important;
}


/*Title description color fix */
.freebirdFormviewerViewItemsSectionheaderDescriptionText{
color: #DDD !important;
}

/* form editing page responses' text color fix*/
.freebirdFormviewerViewItemsTextTextItem .freebirdFormviewerViewItemsTextDisabledText{
color: #DDD !important;
}


/* edit page's question page's background color tweaked to be constantly #bdbdbd disregard choice chosen at color palette button */
.freebirdFormeditorViewEditingsurfaceEl{
background-color:#bdbdbd !important;
}

.docssharedWizSelectPaperselectOption.isPlaceholder{
	background-color:rgb(158, 158, 158);	
}
.docssharedWizSelectPaperselectPopup{
	background-color:rgb(158, 158, 158);
}
.docssharedWizSelectPaperselectOption{
	color:#DDD;
}

/* misc */
.freebirdFormviewerViewMelbaMelba{
	background-color:#555;
	color:#DDD;
}
.freebirdFormeditorViewPageTitleAndDescription,.freebirdFormeditorViewItemContent{
	background-color:#424242 !important;
}

.freebirdFormeditorViewItemContent{
	color:#DDD !important;
}
.freebirdFormeditorViewTabTabList.exportTabList{
	background-color:#757575 !important;
}
.freebirdFormeditorViewEditingsurfaceEl{
	background-color:#212121 !important;
}
.freebirdHeaderMastWithOverlay{
	background-color:#424242 !important;
}

.freebirdThemedTab .exportTab.isSelected,.quantumWizTabsPapertabsTabContent{
	color:#DDD !important;
}

.freebirdFormeditorViewResponsesRecordviewResponseContainer,.freebirdFormeditorViewResponsesRecordviewResponseControlsContainer,.freebirdFormeditorViewResponsesResponsesTabHeaderContainer,.freebirdFormeditorViewResponsesAcceptingResponsesFlexContainer{
	background-color:#646464
}

.freebirdFormeditorViewResponsesRecordviewResponsePosition,.freebirdFormeditorViewResponsesResponsesCount,.freebirdFormeditorViewResponsesRecordviewPositionInput .exportInput,.freebirdFormviewerViewHeaderRecordViewDisclaimer,.freebirdFormeditorViewResponsesEnabledLabel, .freebirdFormeditorViewResponsesAcceptingResponsesFlexContainer.freebirdFormeditorViewResponsesDisabled .freebirdFormeditorViewResponsesDisabledLabel{
	color:#DDD;
}
						
.freebirdFormeditorViewResponsesAcceptingResponsesFlexContainer{
	border-color:#646464
}									

.freebirdThemedTab .exportTab.isSelected .freebirdThemedBadge{
	background-color:#424242
}
