.quantumWizButtonPaperbuttonFocusOverlay{
background-color:#9e9e9e !important;
}
*/
	
/*Edit page send button's send text, keep an eye if this change will unwittingly affect other things*/
.freebirdThemedText{
color:#212121
}




}
