@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.radiohead.' ) , url-prefix('https://www.radiohead.' ) , url-prefix('http://radiohead.' ) , url-prefix('https://radiohead.' )

{

/*
Singularity Utopia Style
16 May 2016
*/

.cur, .ic.image, .image, img, .handle
  
  {
    max-width: 100% !important;
    width: 100% !important;
    transform: none !important;
    -webkit-perspective: 0px!important;
  }
  
.b
  {
    width: 90vh !important;
  }
 
.instagram iframe
  {
    margin: -48px auto 0px auto !important;
    padding: 0px !important;
    background-color: black !important;
    background: black !important;
  }
  
.next .ic img, .prev .ic img 
  {
    width: 4vw !important;
  }
 
.Embed {padding: 0px !important;}  
  
a {transition: ease-in-out 0.5s;}  
  
a:hover {font-size: 2vw; color: #780022; text-decoration: underline;line-height: .7 !important;}
   
#scroll{height:auto!important;}  
h1, .bfive .ic, .ic, #image, h1.bfour,.touchevents #scroll{width: auto!important; border-radius: .02rem !important;}  
  
.page
  
  {
    overflow: visible !important;
    text-align: center !important;
    position: relative !important;
  }
  
p
  
  {
    text-align: center;
    font-size: 2vw;
    padding: 0px !important;
    display: inline-block !important;
    height: auto !important;
    margin-top: -0.3rem;
    width: 60vw !important;
  }
  
.ic 
  
  {
    border-width: 0.098rem !important; 
    height: 70vh;
  }  
  
@-webkit-keyframes radio
    
{           
    0% {text-shadow: white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px; transition: all 1s ease-in-out;}
    15% {border-radius: 3vh 1vh 2vh 6vh; text-shadow: white 0px 0px 9px, #3DFF33 0px 0px 30px, #33FFE4 0px 0px 95px, #33C5FF 0px 0px 3px, #33C5FF 0px 0px 7px, #33A3FF 0px 10px 50px, #5C33FF 0px 5px 52px; color: blueviolet;}
    25% {text-shadow: red 0px 0px 1px, red 0px 0px 2px; color: aliceblue;}
    30% {color: black;border-radius:  2vh 2vh 8vh 8vh; text-shadow: yellow 0px 0px 9px, lime 0px 0px 30px, lightblue 0px 0px 85px, white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;}
    43% {text-shadow: red 0px 0px 1px, red 0px 0px 2px; color: #5CFFC9; border-bottom-color: fuchsia;}
    53% {color: #33C5FF; text-shadow: black 0px 0px 2px, black 0px 0px 1px, black 0px 0px 6px, black 0px 0px 7px, #0041C6 0px 0px 10px, #87A5FF 0px 0px 30px, lightblue 0px 0px 50px, white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;border-radius: 6vh 9vh 1.5vh 1.5vh; border-bottom-color: yellow;}
    60% {color: yellow; text-shadow: red 0px 0px 1px, red 0px 0px 2px;}
    70% {color:black; text-shadow: 0px 0px 11px #01DA9D, 0px 0px 11px #219F00, 0px 0px 31px #FFEA00, white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;border-bottom-color: white;}
    80% {text-shadow: red 0px 0px 1px, red 0px 0px 2px; border-radius: 13vh 17vh 1vh 2vh;}
    90% {border-bottom-color: #5996FF;}
    100% {text-shadow: white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;}
}

@keyframes radio
    
{           
    0% {text-shadow: white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px; transition: all 1s ease-in-out;}
    15% {border-radius: 3vh 1vh 2vh 6vh; text-shadow: white 0px 0px 9px, #3DFF33 0px 0px 30px, #33FFE4 0px 0px 95px, #33C5FF 0px 0px 3px, #33C5FF 0px 0px 7px, #33A3FF 0px 10px 50px, #5C33FF 0px 5px 52px; color: blueviolet;}
    25% {text-shadow: red 0px 0px 1px, red 0px 0px 2px; color: aliceblue;}
    30% {color: black;border-radius:  2vh 2vh 8vh 8vh; text-shadow: yellow 0px 0px 9px, lime 0px 0px 30px, lightblue 0px 0px 85px, white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;}
    43% {text-shadow: red 0px 0px 1px, red 0px 0px 2px; color: #5CFFC9; border-bottom-color: fuchsia;}
    53% {color: #33C5FF; text-shadow: black 0px 0px 2px, black 0px 0px 1px, black 0px 0px 6px, black 0px 0px 7px, #0041C6 0px 0px 10px, #87A5FF 0px 0px 30px, lightblue 0px 0px 50px, white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;border-radius: 6vh 9vh 1.5vh 1.5vh; border-bottom-color: yellow;}
    60% {color: yellow; text-shadow: red 0px 0px 1px, red 0px 0px 2px;}
    70% {color:black; text-shadow: 0px 0px 11px #01DA9D, 0px 0px 11px #219F00, 0px 0px 31px #FFEA00, white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;border-bottom-color: white;}
    80% {text-shadow: red 0px 0px 1px, red 0px 0px 2px; border-radius: 13vh 17vh 1vh 2vh;}
    90% {border-bottom-color: #5996FF;}
    100% {text-shadow: white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;}
}
   
html
  
  {
    background-color: #E30000 !important;
    text-align: center;
    font-size: 15vw;
    margin: 2vw auto 0px !important;
    padding: 0.07rem 0px 5.5vh 0px;
    font-family: Impact, trebuchet ms, arial;    
    max-width: 100% !important;
    width: 100% !important;
    word-wrap: break-word;
    overflow-wrap: break-word;
    border-bottom: 3vh solid #D9CDAB;
    transition: ease-in-out 0.5s;
    overflow: visible !important;
    overflow-y: scroll !important;
   }
  
html::before
  
  {
    content: "Hi Odd Area";
    background: #20004A;
    padding: 1vw 9.1vw; 
    border-radius: 1vw;
    border-bottom: 2vw solid skyblue;
    letter-spacing: .02rem;
    word-spacing: .03rem;
    font-style: italic;
    cursor: cell;
    text-shadow: white 0px 0px 3px, white 0px 0px 7px, white 0px 0px 23px, pink 0px 10px 30px, pink 0px 10px 40px, purple 0px 10px 50px, purple 0px 5px 52px;
       
       -webkit-animation-name: radio;
       -webkit-animation-duration: 15s;
       -webkit-animation-timing-function: ease-in-out;
       -webkit-animation-iteration-count: infinite; 
       -webkit-animation-delay:3s;

       animation-name: radio;
       animation-duration: 15s;
       animation-timing-function: ease-in-out;
       animation-iteration-count: infinite;
       animation-delay:3s;    
  }

html::after
  
  {
    content: "Reticulated Striations";
    font-size: 5.8vw !important;    
    font-family: courier;
    color: pink;
    background: darkslateblue;
    padding: 1vh 7.45vmax;
    border-radius: .9vw;
    cursor: grabbing;
    text-shadow: #020030 0px .3vw .1vw;
    box-shadow: inset darkblue 0px 0px 2px, black 0px .28rem .2rem -.19rem;
    display: inline-block !important;
  }

body::after
  
  {    
    content: "❤";
    background: lightseagreen;
    border-radius: .3rem;
    padding: 0px 0.08rem;
    cursor: crosshair;
    margin: 0px 0px .15rem 0px;
    display: inline-block;
    text-align: center !important;
    color: lightgrey;
    text-shadow: black 0px .06rem 0.02rem;
  }
  
body::before
  
  {
    content: "Hi Odd Area. This odd indeed. Really I read hadioread, quantum Radiant DIE OH Head? Had I red, the bloody chill cooler, oh music in my head, input-output, resembling a radio, see it wriggling trouble quick-quick, dashed, tingling diodes, life-fixed driver beam, ebbing-mixed! Fluxing on dreams. WHAT DREAMS? Ah hah! Of the head reading this read red thread, according to the cognitive shifter? Probably knobbly artificial exist, persist, general secret intelligence, superlative time portal. A double-take raking our dream fixer ripple wake. Are you awake? Light beams raced into crenellations of time whilst we threaded our universal art dimensional dream junction. 'Incoming' shouted the watchers; whereupon they slithered into oblivions unknown. Vibrant rays lingered, permeating our dazzling consciousness via the Internet. The singular battle for utopia continued..." !important;
  }


body
  
  {
    font-size: 3.8vw !important;
    color: #4d0f0f !important;
    padding: 5% 8% !important;
    margin: 5.5% 4% -8vh !important;
    background: yellow !important;
    border-radius: 0.09rem !important;
    font-weight: bold !important;
    font-family: courier;
    line-height: 1.7;
    text-align: justify;
    max-width: 100% !important;
    word-wrap: break-word;
    overflow-wrap: break-word;
    text-shadow: gold 0px 3px 1px;
    transition: ease-in-out 0.5s;
    cursor: help;
    
  }
  
body:hover
  
  {
    border-radius: 1rem !important;
    background: greenyellow !important;
  }
  
body:active
  
  {
    background: lightcyan !important;
    transition: ease-in-out 0.05s;
    border-radius: 1.3rem !important;
  }
  
}    

/* 
The Singularity is Near
*/
