@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pointclouds.org"), domain("docs.pointclouds.org") {
  
  body {
    background-color: #333333; /** for docs **/
    color: #d1d1d1;
  }
  
  #page-content {
    background-color: #333333;
    color: #bcbcbc;
  }
  
  pre {
    background-color: #282828;
  }
  
  /* to make keyword visible for above 'pre' */
  .highlight .nf {
    color: #00adee;
  }
  
  img {
    background-color: #aaa;
  }
  
  /** Note/Message boxes **/
  div.admonition, div.warning {
    background-color: #474747;
  }
  
  div.admonition p.admonition-title, div.warning p.admonition-title {
    background-color: #707070;
  }
  
  /**** News ***/
  
  #content-outer {
    background-color: #333333;
  }
  
  .postmeta {
    background-color: #474747;
    border-color: #595959;
  }
  
  /**** Blog ****/
  
  div.blogpost {
    background-color: #333;
  }
  
  div.blogpost-header {
    background-color: #333;
  }
  
  div.blogbody {
    background-color: #474747;
  }
  
  /**** Jobs ****/
  
  .dataTable tbody .even {
    background-color: #474747;
  }
  
  .dataTable thead th {
    background-color: #6b6b6b;
  }
  
  /* Job Post Overlay */
  
  #fancybox-outer {
    color: #bcbcbc;
    background-color: #282828;
  }
  
  /***** For Docs *****/
  div.header {
    background-color: #333333;
    border-bottom-color: #001;
    background-image: none;
  }
  
  td.memItemLeft, td.memItemRight, .mdescLeft, .mdescRight, .memdoc, .memTemplParams, .memTemplItemLeft, .memTemplItemRight {
    background-color: #282828;
  }
  
  .memproto {
    color: #878f7e;
    background-color: #333333;
    text-shadow: unset;
    background-image: none;
  }
  
  .memname {
    background-color: #333333;
  }
  
  a {
    color: #adb8a1;
  }
  
  div.fragment {
    background-color: #282828;
  }
  
  .paramname {
    color: #8f6262;
  }
  
  /* To make comments visible */
  span.comment {
    color: #7b7b7b;
  }
  
  /* Even rows of lists */
  .directory tr.even {
    background-color: #282828;
  }
  
  /* Table header */
  .fieldtable th {
    background-color: #a5a7a3;
    background-image: none;
  }
  
  /**** Source Code View ****/
  span.lineno {
    background-color: #333333;
  }
  
  a.code, a.code:visited {
    color: #4fa2b3;
  }
  
  span.keyword {
    color: #00b300;
  }
  
  span.keywordtype {
    color: #ac7339;
  }
  
  span.stringliteral {
    color: #0140ff;
  }

}
