css:
@-moz-document url-prefix("https://trello.com/b/"), url-prefix("https://trello.com/c/") {
* {
    transition: all .2s ease-out !important;
}
body {
    background: url("http://subtlepatterns.com/patterns/binding_dark.png") repeat transparent !important;
}
textarea,
.action-comment,
.list-card,
.card-detail-desc p {
    font-size: 16px !important;
    line-height: 1.2 !important;
}
.list {
    background: rgba(100, 100, 100, 0.2) !important;
    color: #FFF;
}
.list-header,
.card-label {
    text-align: center;
}
.badge-text {
    float: none !important;
}
.list-card-operation {
    width: 16px !important;
}
.button-link:hover {
    background: #444 !important;
    border-color: #111 !important;
}
.js-archive-card:hover {
    background: tomato !important;
    border-color: red !important;
}
.open-card-composer,
.js-open-add-list {
    font-size: 0;
    color: #EEE;
}
.open-card-composer:before {
    font-size: 1rem;
}
.open-card-composer:hover,
.js-open-add-list:hover {
    color: #222 !important;
    background: #FFF;
    text-decoration: none;
}
.js-open-add-list:before {
    font-size: 1rem;
}

.list-card {
    background-color: #333339 !important;
    color: #EEE !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.5) !important;
    border-right: 1px solid rgba(0, 0, 0, 0.2) !important;
    border-width: 2px !important;
    border-radius: 0px 3px;
}
.list-card.is-stickered .list-card-details {
	background-color: inherit;
}
.list-card .card-label,
.list-card-members .member,
.list-card-members .member-avatar
{
    width  : 20px;
    height : 20px;
    border-radius: 100%;
}
.list-card a {
    color: #EEE !important;
}
.list .list-cards::-webkit-scrollbar-thumb {
    background: #444;
}
.list .list-cards::-webkit-scrollbar-track-piece {
    background: transparent;
}

.badge.is-unread-notification,
.badge.badge-points
{
    border-radius: 100%;
    width : 30px !important;
    height: 20px !important;
    padding: 0px !important;
    text-align: center;
    float: none;
}

.badge.badge-points {
    background: #777 !important;
}

.badge.is-unread-notification {
    background: #A00;
}   

.list-card-labels .card-label {
    line-height: normal;
    text-overflow: clip;
    text-align: left;
    letter-spacing: 50px;
    padding-left : 5px;
    width : 16px;
}

.list-card-cover {
    opacity: 0.5;
}

.list-card.is-stickered .list-card-stickers-area .stickers {
	height : 30px;
}
.list-card-stickers-area .sticker-image,
.sticker
{
	width  : 25px;
	height : 25px;
}

.list-card.is-stickered .list-card-details {
	margin-top: 30px;
}
}
