css:
@-moz-document url-prefix("https://www.furk.net") {

.navbar-default {
    background-color: #f8f8f8;
    border-color: #e7e7e7;
background: url(http://vignette2.wikia.nocookie.net/kallenbearrp/images/7/73/Seamless-leather-texture5.jpg/revision/latest?cb=20160113115101);
background-repeat: repeat ;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #555;
    cursor: default;
    background-color: #fff;
    border: 1px solid #ddd;
    border-bottom-color: transparent;
    background: rgba(10, 10, 10, 0.80) url(http://vignette1.wikia.nocookie.net/kallenbearrp/images/c/cc/Water_stucco_wall_texture_seamless.jpg/revision/latest?cb=20160113115148) !important;
background-repeat: repeat 
}

body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #bbb;
    background-color: #484141;
    background: url(http://vignette4.wikia.nocookie.net/kallenbearrp/images/5/51/Googletile.png/revision/latest?cb=20160113083017);
    background-repeat: repeat;
}

fieldset {
    border: 1px solid #bbb;
    border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    text-align: left;
    padding: 5px;
    width: auto;
    margin: 0px 0px 5px 0px;
background: url(http://vignette2.wikia.nocookie.net/kallenbearrp/images/c/c1/Metal_tile5.png/revision/latest?cb=20160113115126);
background-repeat: repeat ;
}

.panel-default>.panel-heading {
    color: #333;
    background-color: #f5f5f5;
    border-color: #ddd;
    background: rgba(10, 10, 10, 0.80) url(http://vignette1.wikia.nocookie.net/kallenbearrp/images/c/cc/Water_stucco_wall_texture_seamless.jpg/revision/latest?cb=20160113115148) !important;
background-repeat: repeat 
}

.panel {
background: url(http://vignette2.wikia.nocookie.net/kallenbearrp/images/c/c1/Metal_tile5.png/revision/latest?cb=20160113115126);
background-repeat: repeat ;
}

.form-control {
    color: #555;
    background-color: rgba(255, 255, 255, 0.17);
}

.progress {
    background-color: rgba(255, 255, 255, 0.17);
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #10122F;
}

.list-group-item {
    position: relative;
    display: block;
    padding: 10px 15px;
    margin-bottom: -1px;
    background-color: rgba(230, 52, 52, 0.32);
    border: 1px solid #ddd;
background: url(http://vignette2.wikia.nocookie.net/kallenbearrp/images/c/c1/Metal_tile5.png/revision/latest?cb=20160113115126);
background-repeat: repeat ;
}
}
