css:
@-moz-document domain("calendar.google.com") {
/* global font */
body,
.st-c-pos {    
    font-family: 'Fira Code' !important;
    font-weight: bold;
	font-size: .9em;
}

/* customize font in task sidebar */
.Rb,
.j, .b, .Y {
  	font-family: 'Fira Code' !important;
    font-weight: bold;
	font-size: .9em;
}
  
/* repositize notification box */
#ntowner {
    top: 0;
}

/* res-style task meta section */
.Yb {
  color: #999;
}

/* disable boder color on selecting task  */
tr.kc td {
  	border-color: transparent !important;
}

/* hide the top search bar */
#onegoogbar {
    display: none !important;
}

/* hide close task sidebar button */
.sng-title-bar.goog-zippy-header {
    display: none !important;
}

/* hide toggle task sidebar button */
#rhstogglecell {
display: none !important;
}

/* make calendar table bigger */
#gridcontainer {
    margin-right: 10px !important;
}
  
/* hide task list header */
.Sb {
  	display: none;
}

/* make task sidebar bigger */
#gadgetcell {
    margin-top: 5px !important;
    margin-right: 0 !important;
    width: 320px !important;
  	padding-top: 24px !important;
}
#maincell {
    margin-right: 320px !important;
}

/* make event/task text in calendar table better */
.rb-n {
    height: auto !important;
}
.rb-ni {
    word-wrap: break-word !important;
    white-space: pre-wrap !important;
    text-align: right !important;
}
  .st-bg-today, .st-bg-td-first, .st-bg-td-last {
    border-left: transparent;
  }
  .st-bg-next {
    border-left: transparent;
  }
  .st-dtitle-today {
    border-left: transparent;
    border-top: transparent;
  }
  .st-dtitle-next {
    border-left: transparent;
  }
  .st-dtitle-down {
    border-top: transparent;
  }
}
