css:
@-moz-document domain("developer.android.com") {
*{
		border-color: #585858 !important;
		outline-color: #787e81 !important;
		color: #949c9c;
		text-shadow: none !important;
		-moz-border-radius: 0 !important;
		-webkit-border-radius: 0 !important;
		-ms-border-radius: 0 !important;
		border-radius: 0 !important;
		-moz-box-shadow: none !important;
		-webkit-box-shadow: none !important;
		-ms-box-shadow: none !important;
		box-shadow: none !important;
	}
	
	html,body, select>option:hover, td, #classes-nav, #packages-nav, #side-nav, #devdoc-nav, .ui-resizable-handle, div.sidebox-wrapper, div.sidebox {
		background-image: none !important;
		background-color:#2B2A2A !important;
	}
	
	
	h1{
		color: #70B4CE !important;
	}

	a, h2, h3, h4{
		color:#bad487 !important;
	}

	h5{
		color: #bbb !important;
	}
	
	input {
		color:#839496 !important;
	}

	::selection {
		background: #33b5e5 !important;
		color: #2b2b2b !important;
	}

	::-moz-selection {
		background: #fdf6e3 !important;
		color: #839496 !important;
	}
	::-webkit-selection {
		background: #73855e !important;
		color: #ccd5d6 !important;
	}
	:link {
		color: #bad487!important;
	}
	:visited {
		color: #C996C7!important;
	}

	.morehover>.top {
		background-color: none;
	}
	.morehover>.mid {
		background-color: #333333;
		background-image: url("http://www.colorhexa.com/333333.png");
	}

	.logo>a {
		background: url("data:image/png;base64,") !important;
	}
	.logo>a>img {
		opacity: 0;
		-ms-opacity: 0;
	}

	.jspVerticalBar, .jspHorizontalBar, pre {
		background: transparent !important;
	}

	#devdoc-nav>.jspContainer>.jspPane {
		right: 0 !important;
	}

	li a.selected {
		padding:2px 4px 2px 4px;
		border-radius:2px;
		color:#33B5E5 !important;
	}

	th, div#jd-header, .jd-details-title {
		background-color:#444 !important;
		padding:4px !important;
		border-radius:2px !important;
	}

	span.sympad{
		color: #ddd;
	}
		
	div.jspContainer {
		border:0px !important;
	}

	div#nav-swap {
		border-top:1px solid #181818 !important;
	}

	b {
		color:#c3ee89 !important;
	}
	
	strong{
		font-weight:bold;
		color: #2b2a2a;
	}

	span.new, span.new-child {
		color:#f4f4a1 !important;
	}

	code {
		color:#c8d49c !important;
	}

	.typ {
        color: #B58BB9 !important;
	}

	.pun{
        color: #b6b6b6 !important;
    }

	.str {
        color: #d67171 !important;
    }

	.lit {
        color: #639985 !important;
    }

	.atn {
        color: #ae80ae !important;
    }

	.atv {
        color: #c8d49c !important;
    }

	.com {
        color: #709a68 !important;
    }

	pre {
		background-color: #2f2f2f !important;
	}

	td p{
        background-color: #222222 !important;
    }

	.locales select, .sites select {
	  background: #444444 right center no-repeat;
	}

	#packages-nav li.selected a, #packages-nav li.selected a:active, #packages-nav li.selected
	a:visited,
	#classes-nav li li.selected a, #classes-nav li li.selected a:active, #classes-nav li li.selected
	a:visited,
	#nav-tree li div.selected {
		font-weight: 500;
		color: #222222;
		background-color:#444444;
	}

body{
    background-color: #222222 !important;
    color: gray !important;
}

.nav-section-header{
    color: #606060 !important;
    background-color: #1E1E1E !important;
}

html, .nav-section li, .dac-header-search, .cols, .dac-footer, code, .dac-header-inner, .dac-expand, .dac-section, .procedure-box, p, div .sidebox, ul.dac-nav-secondary, li{
    background-color: #222222 !important;
}

strong{
    color: #B8B8B8 !important;
}

.landing-button{
    color: #202B0B !important;
}

span.pln{
    color: #B1B1B1 !important;
}

span.kwd{
    color: #8a8ab4 !important;
}

span.tag{
    color: #d2a35b !important;
}

.selected a span, li.selected a{
    color: #81b9ce !important;
}

.nav-section.selected.expanded ul li a span{
    color: #8f9798 !important;
}

.nav-section.selected.expanded ul li a{
    color: #b0c97e !important;
}

td small{
    color: white !important;
}
	

	.search_filtered li a {
		color: #839496 !important;
	}
	
	#searchResults .gsc-tabHeader.gsc-tabhInactive,
  #searchResults .gsc-cursor-page {
    background: #444;
    border: 0;
    color: #70B4CE;
  }
	
	#searchResults .gsc-tabHeader.gsc-tabhActive,
	#searchResults .gsc-tabHeader.gsc-tabhActive:hover,
	#searchResults .gsc-cursor-page.gsc-cursor-current-page,
	#searchResults .gsc-cursor-page.gsc-cursor-current-page:hover {
    background-color: transparent;
    border: 1px solid #444;
    color: #70B4CE;
  }
	
	.gs-no-results-result .gs-snippet, .gs-error-result .gs-snippet {
    background-color: #2B2A2A;
		border: none;
	}
	
	.dac-header-console-btn,
	.dac-fab {
		background: #2B2A2A;
	}
	
	.dac-sprite.dac-search {
		-webkit-filter: invert(1);
		filter: invert(1);
	}
	
	.dac-footer .locales select {
		background-color: #444;
		color: #949c9c;
	}
	
	#leftSearchControl td {
		padding: 0.5em 1em;
	}
	
	.search_filtered li.header {
		color: #bad487;
	}
	
	.suggest-card {
		background-color: #2B2A2A;
	}

}
