css:
@-moz-document domain("play.google.com") {
.paper-toolbar-0 {
  display: block;
        position: relative;
        box-sizing: border-box;
        -moz-box-sizing: border-box;

        
        height: 64px;

        background: #673ab7;
        color: white;;
}

.paper-button-1 {
  display: inline-block;
        position: relative;
        box-sizing: border-box;
        min-width: 5.14em;
        margin: 0 0.29em;
        background: transparent;
        text-align: center;
        font: inherit;
        text-transform: uppercase;
        outline-width: 0;
        border-radius: 3px;
        -moz-user-select: none;
        -ms-user-select: none;
        -webkit-user-select: none;
        user-select: none;
        cursor: pointer;
        z-index: 0;
        padding: 0.7em 0.57em;font-weight: 500;
          color: white;
          background-color: #673ab7;
}

#player paper-icon-button[data-id="play-pause"]:not([style-scope]):not(.style-scope):not([disabled]) {
  color: #673ab7;
}

.nav-item-container.selected:not([style-scope]):not(.style-scope),.nav-item-container.selected iron-icon:not([style-scope]):not(.style-scope) {
  color: #673ab7;
}

#player paper-icon-button[data-id="repeat"].active:not([style-scope]):not(.style-scope),#player paper-icon-button[data-id="shuffle"].active:not([style-scope]):not(.style-scope),#player paper-icon-button[data-id="queue"].opened:not([style-scope]):not(.style-scope) {
  color: #673ab7;
}

#player paper-icon-button[data-id="play-pause"]:not([style-scope]):not(.style-scope):not([disabled]) {
  color: #673ab7;
}

.primary:not([style-scope]):not(.style-scope) {
  color: #673ab7;
}

.primary-background:not([style-scope]):not(.style-scope) {
  background-color: #673ab7;
}

.top-charts-view .song-row [data-col="index"] .column-content:not([style-scope]):not(.style-scope),.info-card:not([style-scope]):not(.style-scope):not(.szsb-card):not(.add-your-music) paper-button,paper-toast paper-button[data-action="button"]:not([style-scope]):not(.style-scope),.ups.light iron-icon:not([style-scope]):not(.style-scope),.upload-dialog-dragover .upload-dialog-description:not([style-scope]):not(.style-scope),paper-dialog .buttons paper-button:not([style-scope]):not(.style-scope):not([disabled]) {
  color: #673ab7;
}

.goog-menu .goog-menuitem.selected .goog-menuitem-content:not([style-scope]):not(.style-scope) {
  color: #673ab7 !important;
}

.signup .banner:not([style-scope]):not(.style-scope),.upload-dialog-title:not([style-scope]):not(.style-scope) {
  background-color: #673ab7;
}

.upload-dialog-dragover:not([style-scope]):not(.style-scope) {
  border-color: #673ab7;
}

iron-icon.primary:not([style-scope]):not(.style-scope) {
  color: #673ab7;
}

.paper-progress-0 #primaryProgress.paper-progress {
  background: #673ab7;
}

.paper-progress-0[disabled] #primaryProgress.paper-progress {
  background: #673ab7;
}

.paper-slider-0 #sliderKnobInner.paper-slider {
  margin: 10px;
        width: calc(100% - 20px);
        height: calc(100% - 20px);
        background-color: #673ab7;
        border: 2px solid #673ab7;
        border-radius: 50%;

        -moz-box-sizing: border-box;
        box-sizing: border-box;

        transition-property: -webkit-transform, background-color, border;
        transition-property: transform, background-color, border;
        transition-duration: 0.18s;
        transition-timing-function: ease;
}

}
