css:
@-moz-document url-prefix("http://socialani.me") {
.toggle-search.active {
    background: initial;
    border: none;
    margin-right: 6.7%;
    position: fixed;
    z-index: 999;
    position: fixed;
    left: 90%;
    top: 5%;
    z-index: 20!important;
    background-image: url(http://i.imgur.com/Cm08rGv.png);
background-size: 100% 100%!important;
    font-size: 0;
}
.toggle-search {
    -webkit-box-shadow: initial; 
    box-shadow: initial; 
}
.search-expand-inner {
    padding-top: 3%;
    padding-right: 10%;
    padding-bottom: 25px;
    padding-left: 10%;
}
.search-expand{
    width: 100%;
    height: 100%;
     z-index: 999;
     position: fixed;
    left: 0;
    top: 0;
    background: rgba(51,51,51,0.90);
    z-index: 10;
}
.search-expand div input {
    font-size: 54px;
    border: 0;
    background: transparent;
    -webkit-appearance: none;
    box-sizing: border-box;
    outline: 0;
    font-weight: 200;
    width: 100%;
    color: #e2e2e2;
    height: 100%;
    margin: auto;
    margin-left: 10px;
}
ul.autocomplete li button.more-result-button {
    color: #FFF!important;
    background-color: #3B8DBD;
    width: 100%;
    border: 0;
    cursor: pointer;
    margin: 0;
    padding: 10px 0;
    display: inline-block;
    position: relative;
    line-height: 32px;
    border-radius: 2px;
    font-size: 1.1em;
}

.search-expand-inner div:after {
    font-size: 25px;
    top: 30%;
    left: auto;
}
ul.autocomplete {
    display: none;
    width: 100%;
    background-color: transparent;
    border: initial;
    -webkit-box-sizing: initial;
    box-sizing: initial;
}
ul.autocomplete li.sa {
    padding: 10px;
}
ul.autocomplete li.sa br {
    content: ' /';
}
ul.autocomplete li.sa br:after {
    content: ' / ';
}
ul.autocomplete li.sa p {
    line-height: initial;
    font-size: 2em;
}
}
