css:
@-moz-document domain("zhihu.com") {
/*navi bar logo*/
.zu-top-link-logo {
    background-image:url(http://ww4.sinaimg.cn/large/005Suejwgw1f3ioqixs4bj30bq04iaag.jpg) !important;
    background-size:contain!important;
    background-position:center!important;
    width:80px!important;
}

/*word subsititute*/
.zm-votebar button.up:hover::before {
    content:"吼啊!";
}
.zm-votebar button.up:hover .count {
    display:none;
}

.zm-profile-header-info-list:before {
    content:"很惭愧，只";
}
.zm-profile-header-info-list:after {
    content:"，谢谢大家！";
}

.zm-profile-section-wrap .zm-profile-section-name:before {
    content:"姿势水平";
    visibility:visible;
}
#zh-profile-ask-wrap .zm-profile-section-name:before {
    content:"自我奋斗";
    visibility:visible;
}
#zh-profile-activity-wrap .zm-profile-section-title:before {
    content:"历史的行程";
    visibility:visible;
}
.zm-profile-section-wrap .zm-profile-section-name, .zm-profile-section-wrap .zm-profile-section-title {
    visibility:hidden;
}

div ._InputBox_blur_3JWV:before {
    content:"膜一个！"!important;
}

.zm-editable-editor-field-element p span:before {
    content:"闷声发大财，我什么话都不说，这是坠吼的！";
    visibility:visible;
}
.zm-editable-editor-field-element p span {
    visibility:hidden;
}

/*top nav button*/
.zh-backtotop .btn-action {
    background-image:url(http://ww4.sinaimg.cn/large/005Suejwgw1f3v4k2cwryj308q06s0ut.jpg)!important;
    background-color:transparent!important;
    background-repeat:no-repeat!important;
    background-size:contain!important;
    bottom:-120px!important;
    width:280px!important;
    height:200px!important;
}
.zh-backtotop .btn-action .arrow, .zh-backtotop .btn-action .stick {
    display:none;
}
#zh-tooltip.right {
    transform:translate(-50px,-30px)!important;
}
#zh-tooltip.right .content-prewrap:before {
    content:"你们有一个好，";
}
#zh-tooltip.right .content-prewrap:after {
    content:"比西方记者跑得还快！";
}

/*Theme 江泽之力*/
/*jz main*/
body {
    background:linear-gradient(to bottom, rgba(255,255,255,0.8) 300px, rgba(255,255,255,0) 1200px, rgba(255,255,255,0)), url(http://ww2.sinaimg.cn/large/005Suejwgw1f3v9bmqltxj30dw0dwglr.jpg)!important;
}
.zm-profile-header, .zm-profile-section-wrap, .comment-app-holder {
    background:rgba(255,255,255,0.75);
    box-shadow:0 0 60px white, 0 0 30px white, 0 0 15px white!important;
    border-radius: 5px;
}
.comment-app-holder>div {
    border:none!important;
}
._CommentForm_bordered_2isg{
    background:none!important;
    border:none!important;
}
.icon-spike {
    display:none;
}
.zm-item-rich-text, .post-content, .entry-content{
    background:white;
    margin:10px 0px;
    padding: 15px 20px;
    border-radius: 5px;
    box-shadow:0 0 60px white, 0 0 30px white, 0 0 15px white!important;
}
.zm-item-rich-text img, .post-content img {
    margin: 15px auto!important;
}
.zh-summary img {
    margin: auto 10px auto 0px!important;
}
.zm-meta-panel.goog-scrollfloater-floating {
    width:600px!important;
    background:linear-gradient(to right, rgba(255,255,255,0), rgba(255,255,255,0.9) 10px, rgba(255,255,255,0.9) 580px,rgba(255,255,255,0))!important;
}
#zh-question-collapsed-wrap>div:last-child, .zm-item-answer:last-child {
    border:none!important;
}
._CommentBox_root_G_1m._CommentBox_bordered_3Fo- {
    box-shadow:none!important;
}
#zh-footer {
    background:linear-gradient(to bottom, rgba(255,255,255,0), rgba(255,255,255,0.8) 80%);
}

/*jz nav bar*/
div.zu-top, #top-nav-profile-dropdown a{
    background:url(http://ww1.sinaimg.cn/large/005Suejwgw1f3vgk4rm7uj30dw0dw758.jpg)!important;
    background-size:250px!important;
    border:none;
}
.zu-top-search-input {
    background:rgba(80,80,80,0.6)!important;
    border:1px solid #333!important;
}
.zu-top-search-button {
    background:transparent!important;
    border:none!important;
    box-shadow:none!important;
}
.zu-top-nav-userinfo {
    background:transparent!important;
}
.top-nav-profile .zu-top-nav-userinfo:hover {
    border:#eee 10px!important;
    background:transparent!important;
}
.zu-top-add-question {
    border: 1px solid #666!important;
    border-radius: 5px!important;
    background:transparent!important;
}
.zu-top-nav-ul li {
    background:transparent!important;
}

}
