css:
@-moz-document regexp("http://(fa1|fa2|www)\\.eldarya\\.(com|fr|pl|de|dk|es|hu|it|nl|ro|ru|se|uk)\\/.*") {
/*ФОН*/
    body {
    background: #000000 url("http://static.tumblr.com/eakfy7x/ZR4o6rab6/skin0.png") repeat top center;
    background-size: 477px 242px;
    overflow-y: scroll; 
    }
    body {
    vertical-align: baseline;
    font-family: "trebuchet ms", arial, serif;
    font-size: 14px;
    line-height: 1em;
    color: #1D1D1D;
    min-height: 100%;
    }
    body.night {
    background: #000000 url("http://static.tumblr.com/eakfy7x/ZR4o6rab6/skin0.png") repeat top center;
    background-size: 477px 242px;
    overflow-y: scroll;
    }
 
    /*ВЕРХНЯЯ ПОЛОСА*/
    #header {
    height: 167px;
    position: relative;
    background-color: rgba(0, 199, 170, 0.06);
    }
    #header #header-top {
    position: absolute;
    top: 0;
    background-color: rgba(0, 0, 0, 0.53);
    height: 39px;
    width: 100%;
    }

    /*НИЖНЯЯ ПОЛОСА*/
    #footer {
    font-family: Banda Bold, sans-serif;
    position: absolute;
    height: 42px;
    width: 100%;
    bottom: 0;
    left: 0;
    background-color: rgba(0, 179, 168, 0.06);
    }
    #footer> div:after { content:"| Design by DOHA";}

    /*КОНТЕЙНЕР*/
    #container .page-main-container {                                       
    background: rgba(0, 187, 171, 0.06);
    border-radius: 0px;
    padding: 20px;
    min-height: 400px;
    box-shadow: inset 1px 1px 3px 1px rgba(49, 51, 68, 0);
    }
    #container .page-main-container:after {
    content: '';
    border-radius: 8px;
    background-position: 0 0, 100% 0, 0 0, 0 100%;
    background-repeat: no-repeat;
    background-size: 4px 100%, 4px 100%, 100% 4px, 100% 4px;                                       
    background-image: linear-gradient(180deg, rgba(255, 255, 255, 0) 4%, rgba(210, 213, 249, 0) 14%, rgba(63, 73, 208, 0) 74%, rgba(196, 199, 247, 0) 90%, rgba(255, 255, 255, 0) 97%), linear-gradient(180deg, rgba(255, 255,       255, 0) 4%, rgba(210, 213, 249, 0) 14%, rgba(63, 73, 208, 0) 74%, rgba(196, 199, 247, 0) 90%, rgba(255, 255, 255, 0) 97%), linear-gradient(rgba(255, 255, 255, 0), rgba(255, 255, 255, 0)), linear-gradient(rgba(255, 255,       255, 0), rgba(255, 255, 255, 0));
    box-shadow: 1px 2px 3px 0 rgba(47, 62, 113, 0);
    position: absolute;
    z-index: -1;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    }
    #content {
    padding-bottom: 82px;
    width: 1010px;
    margin: auto;
    margin-top: 20px;
    margin-right: auto;
    margin-bottom: auto;
    margin-left: auto;
    } 

    /*ВЕРХНЯЯ ПАНЕЛЬ ПЕРСОНАЖА*/
    
    #player-headerPortrait {
    width: 112px;
    height: 113px;
    background-image: url("http://static.tumblr.com/eakfy7x/V83o8jmpu/player-portrait-background.png");
    top: 13px;
    right: 12px;
    }
    #player-infos .player-name .player-name-content {
    -webkit-transform: skew(0deg);
    -moz-transform: skew(0deg);
    -o-transform: skew(0deg);
    -webkit-backface-visibility: hidden;
    padding: 0 70px 0 15px;
    }
    #player-infos .player-name {
    color: #fff;
    font-weight: 600;
    font-family: "Banda Regular", sans-serif;
    position: relative;
    height: 32px;
    line-height: 32px;
    display: inline-block;
    font-size: 16px;
    margin: 25px 0;
    border: solid 2px #00e7ce;
    background-color: rgba(0, 0, 0, 0.64);
    -webkit-transform: skew(0deg);
    -moz-transform: skew(0deg);
    -o-transform: skew(0deg);
    -webkit-backface-visibility: hidden;
    }
    #player-infos .player-currencies {
    background-color: rgba(0, 0, 0, 0.55);
    border-radius: 0px;
    height: 25px;
    line-height: 25px;
    font-family: Banda Semi Bold, sans-serif;
    font-weight: 600;
    padding: 0 22px;
    margin-top: 0px;
    border: solid 2px rgba(0, 255, 228, 0.30);
    }
    #pet-headerPortrait #pet-headerPortrait-base {
    position: absolute;
    height: 100%;
    width: 100%;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    background-color: rgba(0, 0, 0, 0.64);
    border: solid 3px rgba(0, 255, 228, 1);                                        
    }
    #header-portraits {
    width: 197px;
    height: 132px;
    background: url("http://static.tumblr.com/eakfy7x/P4Jo8jm6w/png.png") no-repeat right;
    }
    #player-infos {
    background-image: url("http://static.tumblr.com/eakfy7x/P4Jo8jm6w/png.png");
    background-repeat-y: no-repeat;
    height: 125px;
    padding: 0 10px 0 10px;
    text-align: center;
    }
    #header-portraits.nopet {
    background-image: url("http://static.tumblr.com/eakfy7x/P4Jo8jm6w/png.png");
    }
    #player-infos:after {
    content: '';
    position: absolute;
    left: 80%;
    top: 0;
    width: 304px;
    height: 498px;
    background-image: url("http://static.tumblr.com/eakfy7x/N0ro8jnfm/cartoon_red.png");
    z-index: -1;
    }

    /*МЕНЮ*/
    #main-menu li {
    position: relative;
    display: inline-block;
    width: 80px;
    height: 80px;
    border-radius: 50%;
    margin: 0 10px;
    background-position: center;
    }
    #main-menu ul {
    text-align: center;
    white-space: nowrap;
    background-color: rgba(255, 255, 255, 0);
    display: inline-block;
    margin-bottom: 2px;
    padding: 5px 20px;
    position: relative;
    box-shadow: inset 1px 1px 3px 1px rgba(255, 255, 255, 0);
    border-radius: 0px 0px 0 0;
    }
    #main-menu ul:after {
    content: '';
    border-radius: 8px;
    background-position: 0 0, 100% 0, 0 0, 0 100%;
    background-repeat: no-repeat;
    background-size: 4px 100%, 4px 100%, 100% 4px, 100% 4px;
    background-image: linear-gradient(180deg, rgba(255, 255, 255, 0) 4%, rgba(210, 213, 249, 0) 14%, rgba(255, 255, 255, 0) 74%, rgba(196, 199, 247, 0) 90%, rgba(255, 255, 255, 0) 97%), linear-gradient(180deg, rgba(255, 255,     255, 0) 4%, rgba(210, 213, 249, 0) 14%, rgba(63, 73, 208, 0) 74%, rgba(196, 199, 247, 0) 90%, rgba(255, 255, 255, 0) 97%), linear-gradient(rgba(255, 255, 255, 0), rgba(255, 255, 255, 0)), linear-gradient(rgba(255, 255,       255, 0), rgba(255, 255, 255, 0));
    box-shadow: 1px 2px 3px 0 rgba(47, 62, 113, 0);
    position: absolute;
    z-index: -1;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    }                                      
    #main-menu .menu-label span {
    background-color: #E09235;
    padding: 5px 8px;
    border-radius: 20px;
    margin-right: -100%;
    display: inline-block;
    }
     
    /*ивент*/ 
    #main-menu .main-menu-event {
    background-image: url(http://static.tumblr.com/eakfy7x/f9uoli67x/eventh.png);
    }
    #main-menu .main-menu-event:hover, #main-menu .main-menu-event.selected {
    background-image: url(http://static.tumblr.com/eakfy7x/4ljoli67u/eventh-hover.png);
    }                                        
    /*эпизоды*/
    #main-menu .main-menu-episode {
    background-image: url("http://static.tumblr.com/eakfy7x/dQvocdh9w/episode.png");
    }
    #main-menu .main-menu-episode:hover, #main-menu .main-menu-episode.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/Mz2ocdhab/episode-hover.png");
    }
    /*миниигры*/   
    #main-menu .main-menu-minigames {
    background-image: url("http://static.tumblr.com/eakfy7x/gRhocdhn9/minigames.png");
    }
    #main-menu .main-menu-minigames:hover, #main-menu .main-menu-minigames.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/LsFocdhnc/minigames-hover.png");
    }                                         
    /*магазины*/ 
    #main-menu .main-menu-mall {
    background-image: url("http://static.tumblr.com/eakfy7x/w7tocdhjn/mall.png");
    }
    #main-menu .main-menu-mall:hover, #main-menu .main-menu-mall.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/Crrocdhkp/mall-hover.png");
    } 
    /*аукцион*/ 
    #main-menu .main-menu-marketplace {
    background-image: url("http://static.tumblr.com/eakfy7x/g9Kocdhl8/marketplace.png");
    }
    #main-menu .main-menu-marketplace:hover, #main-menu .main-menu-marketplace.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/KyOocdhlb/marketplace-hover.png");
    } 
    /*ранги*/ 
    #main-menu .main-menu-ranking {
    background-image: url("http://static.tumblr.com/eakfy7x/4pMocdhog/ranking.png"); 
    }
    #main-menu .main-menu-ranking:hover, #main-menu .main-menu-ranking.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/bdRocdhoi/ranking-hover.png");
    } 
    /*библиотека*/ 
    #main-menu .main-menu-library {
    background-image: url("http://static.tumblr.com/eakfy7x/Oe4ocdhim/library.png");
    }
    #main-menu .main-menu-library:hover, #main-menu .main-menu-library.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/0iOocdhj6/library-hover.png");
    } 
    /*сообщения*/ 
    #main-menu .main-menu-messaging {
    background-image: url("http://static.tumblr.com/eakfy7x/vJzocdhmd/messaging.png");
    }
    #main-menu .main-menu-messaging:hover, #main-menu .main-menu-messaging.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/qqyocdhmf/messaging-hover.png");
    } 
    #main-menu .main-menu-messaging .menu-label span {
    background-color: #00B2CC;
    }
    /*банк*/ 
    #main-menu .main-menu-bank {
    background-image: url("http://static.tumblr.com/eakfy7x/aUiocdh5w/bank.png");
    }
    #main-menu .main-menu-bank:hover, #main-menu .main-menu-bank.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/5umocdh6s/bank-hover.png");
    } 
    #main-menu .main-menu-bank .menu-label span {
    background-color: #3FBE78;
    }
    #main-menu li a:hover:after {
    top: -5px;
    right: -5px;
    bottom: -5px;
    left: -5px;
    -webkit-box-shadow: inset 0 0 3px #fff, inset 0 0 16px 1px #fff, 0 0 3px #fff, 0 0 6px #fff;
    -moz-box-shadow: inset 0 0 3px #fff, inset 0 0 16px 1px #fff, 0 0 3px #fff, 0 0 6px #fff;
    box-shadow: inset 0 0 3px #00FFBD, inset 0 0 5px 1px #00FFBD, 0 0 3px #00FFBD, 0 0 6px #00FFBD;
    }
    
    /*НЕРАБОТАЮЩИЕ КНОПКИ МЕНЮ*/
    #main-menu .main-menu-minigames.disabled, #main-menu .main-menu-minigames.disabled:hover, #main-menu .main-menu-minigames.disabled.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/VyQocdea4/minigames-off.png");
    }
    #main-menu .main-menu-minigames.disabled .menu-label span {
    background-color: #444444;
    }

    #main-menu .main-menu-marketplace.disabled, #main-menu .main-menu-marketplace.disabled:hover, #main-menu .main-menu-marketplace.disabled.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/WHPocde9b/marketplace-off.png");
    }
    #main-menu .main-menu-marketplace.disabled .menu-label span {
    background-color: #444444;
    }

    #main-menu .main-menu-ranking.disabled, #main-menu .main-menu-ranking.disabled:hover, #main-menu .main-menu-ranking.disabled.selected {
    background-image: url("http://static.tumblr.com/eakfy7x/ALEocdeb4/ranking-off.png");
    }
    #main-menu .main-menu-ranking.disabled .menu-label span {
    background-color: #444444;
    }


    /*СЛАЙДЕР*/
    .flavr-button.purple, .button.button.button-purple {
    background-color: rgba(0, 0, 0, 0.46);
    border-color: rgba(0, 255, 228, 0.9);
    }
    .flavr-button.purple:hover, .button.button.button-purple:hover {
    background-color: rgba(0, 255, 228, 0.5);
    color: #ffffff;
    }
    
    .index-carousel-prev {
    background: url("http://static.tumblr.com/eakfy7x/QfVo6r8pb/________.png");
    left: 9px;
    }
    .index-carousel-next {
    background: url("http://static.tumblr.com/eakfy7x/hJwo6r8pn/__________.png");
    right: 9px;
    }

    .index-carousel {
    position: relative;
    height: 231px;
    width: 522px;
    border-radius: 0px;
    border: 2px solid rgba(0, 255, 228, 0.9);
    background-color: rgba(0, 0, 0, 0.46);
    overflow: hidden;
    }
    .index-carousel-title {
    font-family: Magnel, serif;
    font-size: 22px;
    line-height: 30px;
    font-weight: 400;
    color: rgba(255, 197, 1, 0.99);
    text-align: left;
    margin-left: 15%;
    }
    .index-carousel-subtitle {
    border-top: 1px solid rgba(255, 197, 1, 0.99);
    font-family: "Banda Semi Bold", serif;
    font-size: 18px;
    line-height: 18px;
    color: rgba(0, 255, 228, 0.9);
    padding: 5px 0;
    text-align: left;
    margin-left: 15%;
    }
    .index-main-container .index-carousel-description {
    color: rgb(255, 255, 255);
    }
    .index-carousel-description {
    margin-top: 15px;
    color: #00E4FF;
    font-size: 13px;
    line-height: 18px;
    text-shadow: 0 0 1px white;
    font-family: "Banda regular", serif;
    text-align: justify;
    height: 80px;
    }

    /*РАНГИ И ТЕМЫ ФОРУМА НА ГЛАВНОЙ*/
    #ranking-list {
    background: -webkit-linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0) 76%, rgba(200, 183, 248, 0) 96%);
    background: -moz-linear-gradient(90deg, #6c469f 0%, #9d8ce4 76%, #c8b7f8 96%);
    background: -o-linear-gradient(90deg, #6c469f 0%, #9d8ce4 76%, #c8b7f8 96%);
    background: -ms-linear-gradient(90deg, #6c469f 0%, #9d8ce4 76%, #c8b7f8 96%);
    background: linear-gradient(0deg, rgba(108, 70, 159, 0) 0%, rgba(157, 140, 228, 0) 76%, rgba(200, 183, 248, 0) 96%);
    }
    .tile-title {
    height: 37px;
    color: #f7bf01;
    line-height: 37px;
    font-size: 17px;
    font-family: Magnel, sans-serif;
    font-weight: 400;
    text-align: center;
    text-shadow: 1px 2px 1px #040404;
    }
    #index-ranking {
    float: left;
    display: inline-block;
    height: 344px;
    width: 280px;
    background-color: rgba(0, 0, 0, 0.17);
    border-radius: 0px;
    border: 2px solid rgba(0, 255, 228, 0.9);
    overflow: hidden;
    margin: 20px 20px 0 0;
    }
    #ranking-list > div + div {
    border-top: 2px solid rgba(0, 255, 228, 0.9);
    }
    #index-news {
    display: inline-block;
    vertical-align: top;
    height: 344px;
    width: 218px;
    margin: 20px 0 0;
    background-color: rgba(63, 221, 215, 0.32);
    border-radius: 0px;
    border: 2px solid rgba(0, 255, 228, 0.9);
    overflow: hidden;
    } 
    #news-list .news + .news {
    border-top: 2px solid rgba(0, 255, 228, 0.9);
    }
    #news-list {
    background: -webkit-linear-gradient(90deg, rgba(0, 0, 0, 0.6) 0%, rgba(0, 0, 0, 0.6) 100%);
    background: -moz-linear-gradient(90deg, #94b6f5 0%, #d3e2fc 100%);
    background: -o-linear-gradient(90deg, #94b6f5 0%, #d3e2fc 100%);
    background: -ms-linear-gradient(90deg, #94b6f5 0%, #d3e2fc 100%);
    background: linear-gradient(0deg, rgba(0, 0, 0, 0.4) 0%, rgba(0, 0, 0, 0.88) 100%);
    }
    #index-news .date {
    float: left;
    background-color: #f7bf01;
    min-width: 55px;
    color: #000;
    border-radius: 25px;
    font-size: 14px;
    text-align: center;
    line-height: 19px;
    margin: 15px 5px;
    font-weight: 700;
    font-family: "Banda bold", sans-serif;
    }
    #index-news .text a {
    color: rgba(0, 255, 228, 0.54);
    }
  
    /*МАГАЗИНЫ*/

    #mall-clothes {
    background: url("http://static.tumblr.com/eakfy7x/bbbocdiuf/vetements-2.png");
    }
    #mall-pets {
    background: url("http://static.tumblr.com/eakfy7x/xstocdj11/familiers.png");
    }

    #mall-alchemy {
    background: url("http://static.tumblr.com/eakfy7x/vj8ocdj1h/alchimiste.png");
    }

    #malls-list li {
    float: left;
    top: 30px;
    width: 283px;
    height: 465px;
    margin: 10px;
    min-height: 100px;
    border-radius: 0px;
    box-shadow: 0 -2px 3px rgba(0, 255, 228, 0.9);
    position: relative;
    text-align: center;
    }

    #mall-alchemy .mall-npc {
    background-size: 100%;
    width: 0px;
    height: 0px;
    position: absolute;
    right: -140px;
    top: -40px;
    }


    #mall-pets .mall-npc {
    background-size: 100%;
    width: 0px;
    height: 0px;
    position: absolute;
    right: 250px;
    top: 100px;
    }

    #mall-clothes .mall-npc {
    background-size: 100%;
    width: 0px;
    height: 0px;
    position: absolute;
    right: 250px;
    top: 100px;
    }

    #malls-list li.ad {
    margin: -1000px -300px;
    box-shadow: none;
    }
    #malls-list .button {
    margin-top: 200px;
    font-size: 30px;
    }
    #malls-list li h2 {
    color: rgb(255, 255, 255);
    padding: 8px 0 2px;
    margin: 0 28px;
    border-bottom: 2px solid rgba(255, 255, 255, 0);
    text-align: left;
    }
    #mall-clothes-npc {
    background: url("http://static.tumblr.com/eakfy7x/qGHocdiwc/in-vetements-2.png") no-repeat;
    background-size: 100%;
    top: 40px;
    width: 283px;
    height: 465px;
    overflow: visible;
    position: absolute;
    z-index: 1;
    }
    #mall-pets-npc {
    background: url("http://static.tumblr.com/eakfy7x/npIocdj20/in-familiers.png") no-repeat;
    background-size: 100%;
    top: 40px;
    width: 283px;
    height: 465px;
    overflow: visible;
    position: absolute;
    }
    #mall-alchemy-npc {
    background: url("http://static.tumblr.com/eakfy7x/ONmooo923/in-alchimiste.png") no-repeat;
    background-size: 100%;
    top: 40px;
    width: 283px;
    height: 465px;
    overflow: visible;
    position: absolute;
    }
    .mall-category, .mall-product, .mall-variation {
    width: 106px;
    background: rgba(34, 134, 121, 0.2);
    margin: 5px;
    padding: 7px;
    float: left;
    cursor: pointer;
    position: relative;
    border-radius: 0px;
    box-shadow: 0 0 5px black;
    }
    .mall-category-title {
    margin: 0 auto;
    font-family: "Banda Bold";
    font-size: 13px;
    line-height: 11px;
    text-transform: uppercase;
    color: rgb(255, 197, 1);
    text-align: center;
    }
    .mall-category:hover, .mall-product:hover, .mall-variation:hover {
    background: rgba(0, 0, 0, 0.71);
    }
    #mall-productDetail-info {
    width: 626px;
    clear: both;
    height: 85px;
    background: rgba(0, 0, 0, 0.31);
    margin: 7px 5px;
    padding: 7px;
    position: relative;
    border-radius: 0px;
    box-shadow: 0 0 5px black;
    text-align: center;
    }
    .mall-productDetail-name {
    margin: 0 auto;
    text-align: center;
    font-family: "Banda Regular", serif;
    text-transform: uppercase;
    color: rgb(255, 197, 1);
    }
    #mall-productDetail-info-gold, #mall-productDetail-info-maana {
    margin: 15px 0;
    width: 160px;
    display: inline-block;
    font-family: "Banda Regular", serif;
    font-size: 16px;
    color: rgb(255, 197, 1);
    }
    .mall-product-maana-price, .mall-product-gold-price, .mall-variations-owned {
    font-family: "Banda Bold", serif;
    font-size: 16px;
    text-transform: uppercase;
    color: rgb(255, 197, 1);
    text-align: center;
    }

    /*ЛОГОТИП*/
    #logo {
    display: block;
    left: 0px;
    top: -0px;
    z-index: 2;
    position: absolute;
    }


    img[src*="static/fr/logo.png"] { 
	padding: 0 0 180px 374px; 
	width:  0; 
	height: 0; 
	background: url("http://static.tumblr.com/eakfy7x/RvUocdjby/logo.png");
        }
        #logo#logo {
        left: -80px;
        }
    img[src*="static/ru/logo.png"] { 
	padding: 0 0 180px 374px; 
	width:  0; 
	height: 0; 
	background: url("http://static.tumblr.com/eakfy7x/RvUocdjby/logo.png");
        }
        #logo#logo {
        left: -80px;
        }


    /*ЭПИЗОДЫ*/
    #episode-list > li .episode-description p, #episode-summary .episode-description p {
