css:
@-moz-document url-prefix("http://socialani.me/@") {
button.fol {
  position: relative;
  display: block;
  padding: 0;
  overflow: hidden;  
  border-width: 0;
  outline: none;
  border-radius: 2px;
  box-shadow: 0 1px 4px rgba(0, 0, 0, .6);
  transition: background-color .3s;
  color: #FFF;
}

button.fol > * {
  position: relative;
}

button.fol:before {
  content: "";
  color: #FFF;
  position: absolute;
  top: 50%;
  left: 50%;
  display: block;
  width: 0;
  padding-top: 0;    
  border-radius: 100%;  
  -webkit-transform: translate(-50%, -50%);
  -moz-transform: translate(-50%, -50%);
  -ms-transform: translate(-50%, -50%);
  -o-transform: translate(-50%, -50%);
  transform: translate(-50%, -50%);
}

button.fol:active:before {
  width: 120%;
  padding-top: 120%;
  transition: width .2s ease-out, padding-top .2s ease-out;
}

button.fol.nofollow{
  background-color: #ff7c00;
}

button.fol.follow.nofollow:focus {
    background-color: #DA6A00;
    color: #FFF;
}

button.fol.nofollow:before {
    background-color: rgba(218, 106, 0, 0.5);
}


button.fol.follow{
  background-color: #3C8800;
}

button.fol.unfollow{
  background-color: #D24D33;
}


button.fol.unfollow:before {
    background-color: rgba(175,64,42,0.5);
}
}
