css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("minecraft.net") {
body[ng-app="MarketPlace"] .popularPanelHomePage, body[ng-app="MarketPlace"] .bg-wool-light, body[ng-app="MarketPlace"] .bg-wool-lighter, #order1, .virtual-reality .grow,.bg-wool-light-image,div.page-section-container:nth-child(3) > div:nth-child(1),div.responsivegrid:nth-child(2) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1),div.page-section-container:nth-child(2) > div:nth-child(1), .introSection, .sectionBlock, #desBox {
    background: none !important;
}
#bgimage, body.basicpage, .container-fluid, .bg-white, .bg-wool-light,.bg-wool-light-image, .bg-200, body[ng-app="MarketPlace"], .educator-panel, #skrollr-body, .introSection, .sectionBlock, .buttons-block {
    background: url(http://www.1mage.fr/images/0729517a901dbcb.png) !important;
}

.nav-link[aria-selected=false], .form-group label, .help__select-support__label, li, .attribution, .font-weight-light, h6, a.minecraftsvg, .ng-binding, .media-block-arrow-link, .vr, .striped-list-title {
    color: #CCC!important;
}

.container-divider {
    display : none;
}

.bg-white p, .form-group input, .btn-link, p, .catalog-list .catalog-item p.rating span, h3.subtitle, p.lead, h5 {
    color: #F5C068 !important;
}

.bg-white h1, .bg-white h2, .bg-white h3, .bg-white h4, h1, h2, .heading_block h3, .content-wrapper h3, .col-8 h3, .media-block-text-holder h3, h3#feature-heading, .eduPanelHead{
    color: #FFFF00 !important;
}
.form-group input {
    fill:white;
    opacity:1;
}

.svg-icon svg {
    fill:#FFFF00;
}

.Imagewrapper {
    display : none;
}

p.no-margin, #downloadhelp h2, #downloadhelp p, h1.view-title, html body.page.basicpage div.root.responsivegrid div.aem-Grid.aem-Grid--12.aem-Grid--default--12 div.generic-container.aem-GridColumn.aem-GridColumn--default--12 main.site-body div div.aem-Grid.aem-Grid--12.aem-Grid--default--12 div.store-page-component.aem-GridColumn.aem-GridColumn--default--12 div.store div div.aem-Grid.aem-Grid--12.aem-Grid--default--12 div.page-section-container.aem-GridColumn.aem-GridColumn--default--12 div.page-section-bg-image.page-section.page-section--half-spacing.false div.container div.aem-Grid.aem-Grid--12.aem-Grid--default--12 div.experiencefragmentrenderer.experiencefragment.aem-GridColumn.aem-GridColumn--default--12 div.xfpage.page.basicpage div.xf-content-height div.aem-Grid.aem-Grid--12.aem-Grid--default--12 div.device-list.aem-GridColumn.aem-GridColumn--default--12 div div.container div.js-devices-selection.device-selection div.row.justify-content-center div.col-md-10 h2#pick-device.text-center, .container .js-device-edition p, #app-login h1, label.d-block, #authortextalign.chat-box p, .mapcontent p,.resource-title, .lesson-description, #lesson-plan p {
    color:black !important;
}

symbol {
    fill:black;
}

.feedList a,.sectionBlock .quick-link {
    color: rgba(54, 176, 48, 1);
}

.footer {
    border-top:0 !important;
}

.post-meta, strong {
    color: #DDD;
}
}
