css:
@-moz-document domain(trello.com) {

body {
  background: #f8f8f7 !important;
  font-family: "Gotham", "Proxima Nova", "Gotham", "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;
  font-size: .95em;
}

button,
html,
input,
select,
textarea {
  font-family: "Gotham","Proxima Nova", "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;
}

.header-woof {
  display: none !important
}

#header {
  padding: 10px !important;
}

.js-open-board {
  background: #979798 !important;
  font-weight: 300 !important;
}

.board-canvas {
  padding-top: 20px
}

.thumbnail {
  background: #979798 !important
}

.sidebar-section-header-title {
  text-decoration: none !important;
  font-weight: 300 !important
}

#search-text {
  border-radius: 0px !important
}

.board-header {
  background: #333;
  margin-bottom: 10px;
}

.header-btn:hover {
  background: none !important;
}

.header-user {
  top: 10px !important;
  right: 5px !important;
  font-weight: 300 !important
}

.header-btn-text {
  font-weight: 300 !important
}

.text {
  text-decoration: none !important;
}

.header-member {
  background: none !important;
}

.member {
  border-radius: 20px !important;
  border: none !important;
  background: none !important;
}

.member img {
  border-radius: 0 !important;
  border: none !important;
}

.list-header-name,
.card-label {
  text-transform: uppercase;
  letter-spacing: .1em;
  font-size: .8em;
  text-shadow: none;
}

.card-label.mod-card-front{
  height: 5px;
}

  .list-card-title{
	display: inline;
}

.list-header-name {
  margin: 0 !important;
}

.list-area {
  margin-top: 30px !important;
}

.list-wrapper {
  width: 350px;
}

.list {
  background: #fff !important;
}

.list-card {
  margin-bottom: 0;
  border-radius: 0;
  max-width: 100%;
  border-bottom: 1px solid #eee;
}

.list-card.active-card {
  border-bottom: 1px solid #eee !important;
}
  
.list-card-details {
	margin-bottom: 4px !important;
}

.list-card-details .badges {
  float: none;
  display: inline;
  margin-left: 4px;
}

.badge {
    min-height: 15px;
}

.badge,
.icon-lg.mod-inline, 
.icon-sm{
	line-height: 16px;
  font-size: 8pt;
  height: 16px;
}

.add-list {
  background: none !important;
}

.add-list:hover {
  background: #fff !important;
}

.list-card-members {
  display: none;
}

.open-card-composer:hover {
  text-decoration: none !important;
  background: none !important;
}

.window-wrapper {
  background: #fff !important;
}

.window-wrapper h3 {
  font-weight: normal !important
}

.window-wrapper a {
  text-decoration: none !important
}
  
}
