css:
@-moz-document domain("codewalr.us") {
body {
    background: url("http://i.imgur.com/WqBlOpW.jpg");
    /* Change the URL to a image you would like, but remmeber to keep the quotes */
    
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}
#header {
    border-top: 1px solid rgba(255, 255, 255, 0) !important;
    border-right: 1px solid rgba(255, 255, 255, 0) !important;
    border-left: 1px solid rgba(255, 255, 255, 0) !important;
    background: rgba(255, 255, 255, 0.38) !important;
    padding-left: 0px;
}
#header div.frame {
    border-top: 1px solid rgba(0, 64, 128, 0) !important;
    border-right: 1px solid rgba(0, 64, 128, 0) !important;
    border-left: 1px solid rgba(0, 64, 128, 0) !important;
    display: block;
    padding: 10px 20px 1em 0;
}
#content_section {
    border-right: 1px solid rgba(255, 255, 255, 0) !important;
    border-left: 1px solid rgba(255, 255, 255, 0) !important;
    background: rgba(255, 255, 255, 0.37) !important;
    padding-left: 0px;
}
#content_section div.frame {
    border-right: 1px solid rgba(0, 64, 128, 0) !important;
    border-left: 1px solid rgba(0, 64, 128, 0) !important;
    display: block;
    padding: 0 20px 0 20px;
}
.windowbg,
#preview_body {
    color: #000;
    background-color: rgba(231, 234, 239, 0) !important;
}
.windowbg span.botslice {
    display: block;
    font-size: 5px;
    line-height: 1px;
    border-bottom: 1px solid rgba(255, 255, 255, 0) !important;
}
#footer_section div.frame {
    border-right: 1px solid rgba(0, 64, 128, 0) !important;
    border-left: 1px solid rgba(0, 64, 128, 0) !important;
    border-bottom: 1px solid rgba(0, 64, 128, 0) !important;
    display: block;
    padding: 20px 0px 1px 10px;
}
#footer_section {
    border-right: 1px solid rgba(255, 255, 255, 0) !important;
    border-left: 1px solid rgba(255, 255, 255, 0) !important;
    border-bottom: 1px solid rgba(255, 255, 255, 0) !important;
    text-align: center;
    background: rgba(255, 255, 255, .38) !important;
    padding-left: 0px;
}
#main_menu {
    padding: 5px 0px 3px 0px;
    margin: 0;
    float: left;
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 20;
    background: rgba(255, 255, 255, 0.65) !important;
    border-bottom: 3px solid #6EB489;
    box-shadow: 1px 2px 3px 0px #004080;
}
a:link,
a:visited {
    color: #800;
    text-decoration: none;
}
.roundframe {
    background: rgba(240,244,247,0) !important;
}
.description, .description_board, .plainbox {
    padding: 0.5em 1em;
    font-size: 0.9em;
    line-height: 1.4em;
    border: 1px solid rgba(187, 187, 187, 0) !important;
    background: rgba(245, 245, 240, 0);
    margin: 0.2em 1px 1em 1px;
}
textarea {
    font: 95%/115% Arial, Helvetica, sans-serif;
    color: #000;
    background: rgba(255, 255, 255, 0.59) !important;
    border: 1px solid #7f9db9;
    padding: 2px;
}
tbody.content td.info a.subject {
    font-weight: bold;
    font-size: 110%;
    color: rgb(23, 0, 134) !important;
}
    .windowbg2 {
    color: #000;
    background-color: rgba(240, 244, 247, 0) !important;
}
blockquote.bbc_standard_quote {
    background-color: rgba(215, 218, 236, 0.65) !important;
}
.poster h4, .poster h4 a {
    color: #00822A !important;
}
}
