css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {
    
    /*header*/
    #header_wrapper{
         background-image: linear-gradient(175deg,  #252525, #4b4b4b);
    }
    
    #duckbar_static .zcm__link{
        color: #d9d9d9 !important;
    }
    
    #duckbar_static .zcm__link:hover{
        color: white !important;
    }
    
    /*search input*/
    .search--header{
        background-color: #acacac !important;
    }
    
    .search, .search--adv{
        border-radius: 0px;
        border-color: #777777;
    }
    .search--adv.search--header.has-text.search--focus{
       border: 1px solid #bcbcbc;
    }
    
    .search__input--adv{
        margin-left: 2%;
        color: #1b1b1b;
        font-size: 1.2em;
  
        letter-spacing: 0.02em;
        font-family: "Lucida Sans Unicode", "verdana" !important;
    }
       
    .search__button:hover, .search__button:focus, .search:hover .search__button:focus, .search--header.has-text.search--hover .search__button:hover, .search--header.has-text.search--focus .search__button:hover, .search--home.has-text .search__button:focus, .search--home.has-text .search__button:hover .search:hover .search__button, .search__input:focus ~ .search__button, .search--header.has-text.search--hover .search__button, .search--header.has-text.search--focus .search__button, .search--home.has-text .search__button {
        background-color: #6a6a6a !important;
        color: #d4d4d4 !important;
        border-radius: 0px;
    }
    
    /*corp*/
    .content-wrap{
        background-color: #efefef;
    }
    @media screen and (min-width: 1280px){
        .cw{
            margin-left: 10%;
            margin-right: 0px !important;
       }
    }
    @media screen and (min-width: 980px){
        .cw{
            margin-left: 6%;
            margin-right: 0px !important;
        }
    }
    
    .results-wrapper{
         padding-top: 2.2em;
    }
   

    /*home page*/
    #pg-index{
        background-image:radial-gradient(#bababa, #424242);
    }
    
    #search_form_homepage{
        background-color: #777777;
        
    }
    
    #search_form_input_homepage{
        margin-left: 3%;
        color: #f9f9f9;
        font-size: 1.4em;
        height: 2.15em;
  
        letter-spacing: 0.05em;
        font-family: "Lucida Sans Unicode", "verdana" !important;
    }
    
    .tag-home,  .tag-home--slide,  .no-js__hide,  .js-tag-home{
        display: none;
    }
}
