style_setting_options.value:
@-moz-document url-prefix("http://www.eldarya.fr/"), regexp("http://(fa1|fa2)\\.eldarya\\.fr/.*") {
/* PET */

/*male*/

img[src="/static/img/design/m.png"] {
    width : 0 !important ;
    height : 0 !important ;
    background : url("http://img15.hostingpics.net/pics/21275116711012m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
}

/*pet info*/

#pet-info-main {
    padding: 15px;
    background-color: #34386f;
    border-radius: 10px;
    position: relative;
    border: 1px solid rgb(0,238,248)!important ;
}

/*affection*/

#pet-info-affection-level div {
    height: 25px;
    background: url('http://img15.hostingpics.net/pics/370787affectionfull.png');
}

#pet-info-affection-level {
    display: inline-block;
    width: 150px;
    height: 23px;
    background: url('http://img15.hostingpics.net/pics/806978affectionempty.png');
}

/*luck*/

#pet-info-luck-level {
    display: inline-block;
    width: 30px;
    min-height: 30px;
    padding-left: 35px;
    background: url('http://img15.hostingpics.net/pics/292218278157luckfull.png') no-repeat left;
    margin-left: 6px;
}

/*evolution*/

#pet-info-evolve div.on {
    color: #ffffff;
    font-weight: bold;
    text-shadow: 0px 0px 2px #feac11, 0px 0px 3px #feac11 !important ;
}

/*fond*/

#pet-info-food, #pet-info-levels {
    background: rgba(116, 149, 186, 0.7) !important ;
}


/*energy*/

#pet-info-energy-level,
#event-bonus-progressbar-fill {
    background-color: #fa9f11;
}

/*explo*/

#messageBarLevel {
    background-color: #fa9f11;
}


#pet-info-food img {
    float: left;
    width: 70px;
    height: 70px;
    border-radius: 4px;
    border: 2px solid #ebc518;
    background: rgb(240, 227, 201);
}
}

