@-moz-document url("http://plantsvszombies.wikia.com/wiki/Special:Chat") {
/* Chat Code */
 
#WikiaPage {
border:1px solid #3d454d;
background-color:rgba(0, 0, 0, 0.75);
}

body.ChatWindow {
background: url(https://feen.us/v66j.png);
background-repeat: repeat;
}

body.ChatWindow .you .avatar {
    border: 1px solid lightslategray; border-radius: 2px 6px; box-shadow: 0 0 6px lightslategray;
    animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

body.ChatWindow .avatar {
    border: 1px solid darkslategray; border-radius: 6px 2px; box-shadow: 0 0 6px darkslategray;
    animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.ChatWindow a { color: royalblue;
}

.ChatWindow #Chat_211 {
    border: solid #3d454d;
    border-width: 0 1px 1px 0;
}

.ChatWindow #ChatHeader, .ChatWindow #WikiaPage {
    border: 1px solid #3d454d;
}

.ChatWindow #ChatHeader { 
    background: rgba(10, 10, 10, 0.80) url(http://vignette2.wikia.nocookie.net/kallenbearrp/images/d/df/Retro_stucco_wall_texture_seamless.jpg/revision/latest?cb=20160115055721) fixed;
background-repeat:repeat;
}

.ChatWindow .WikiaPage {
background: rgba(0,0,0,.8) !important;
}

.ChatWindow .you {
background: transparent;
border-right: 3px ridge lightslategray;
border-left: 3px ridge lightslategray;
border-style: double;
animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}
 
.ChatHeader .User {
border-left:1px solid #3d454d;
animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.ChatHeader .User img {
border: 1px solid white; border-radius: 2px 6px; box-shadow: 0 0 4px white;
animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.ChatHeader .User .username {
font-weight: bold;
color: white;
background: transparent;
}

.ChatWindow #ChatHeader .username {
    color: white;
}
 
.Chat {
color: #d5d4d4;
border-right:1px solid #3d454d;
border-bottom:1px solid #3d454d;
background: url(https://feen.us/pw7x.png);
background-repeat: no-repeat;
}

.Chat .inline-alert {
    color: white;
    font-size: 9px;
    height: auto;
    line-height: normal;
    font-weight: normal;
}

.Chat .time {
    color: darkslategray;
}

.Chat .username {
    color: darkslategray;
    font-weight: bold; 
    text-shadow:0px 0px 2px black;
}
 
.ChatWindow .ChatHeader {
background-color:rgba(0, 0, 0, 0.75);
border:1px solid #3d454d;
