-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}
 
.Rail .public .chevron { 
border-color:lightslategray transparent transparent transparent; 
animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}
 
.Rail .public .chevron.closed { 
border-color:transparent transparent transparent orange; 
 animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}
 
.Rail .private {
border-top: 1px solid #3d454d;
border-bottom: 1px solid #3d454d;
background-color:transparent; 
background-image:-webkit-gradient(linear,0% 0%,0% 0%,color-stop(0,transparent),color-stop(0,transparent));
animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}
 
.Rail .selected { 
background:transparent; 
box-shadow: inset 0 0 0px 0px #000000; -webkit-box-shadow: inset 0 0 0px 0px #000000; 
animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.Rail .User.selected {
    background: transparent;
    animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.Rail .User:hover, .Rail .User.selected:hover {
    background: rgba(61, 69, 77, 0.44);
    box-shadow: none;
    border-color: transparent;
    animation: GreyGlowing 0s linear 0s infinite !important;
    -webkit-animation: GreyGlowing 0s linear 0s infinite !important;
    -moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.Rail .User img {
    border: 1px solid darkslategray; border-radius: 6px 2px;  box-shadow: 0 0 6px darkslategray;
    width: 28px;
    height: 28px;
    left: 10px;
    top: 2px;
    animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
-moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.UserStatsMenu .info img {
    border: 1px solid darkslategray; border-radius: 6px 2px;
    animation: GreyGlowing 0s linear 0s infinite !important;
    -webkit-animation: GreyGlowing 0s linear 0s infinite !important;
    -moz-animation: GreyGlowing 0s linear 0s infinite !important;
}

.UserStatsMenu .actions ul li
.label {
color: royalblue;
cursor: pointer;
margin: 0;
padding: 5px 10px;
line-height: 16px;
white-space: nowrap;
animation: GreyGlowing 0s linear 0s infinite !important;
-webkit-animation: GreyGlowing 0s linear 0s infinite !important;
