/* transparent chat buttons (chat hacks buttons) */

a.wikia-button, .wikia-single-button a, .wikia-menu-button, input[type="submit"], input[type="reset"], input[type="button"], .button, button {
    background-color: rgba(0, 0, 0, 0.03);
    background-image: -moz-linear-gradient(top,#1a1a1a 35%,#000 65%);
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(35%,rgba(26, 26, 26, 0.05)),color-stop(65%,rgba(0, 0, 0, 0.04)));
    background-image: -o-linear-gradient(top,#1a1a1a 35%,#000 65%);
    background-image: -ms-linear-gradient(top,#1a1a1a 35%,#000 65%);
    border: 1px solid rgba(0, 0, 0, 0.05);
    border-top-color: rgba(0, 0, 0, 0.0470588);
    border-top-style: solid;
    border-top-width: 1px;
    color: rgba(65, 105, 225, 0.89);
}

/* SCROLLBAR */

::-webkit-scrollbar {
  width: 3px;
  height: 3px;
}

::-webkit-scrollbar-thumb:vertical, ::-webkit-scrollbar-thumb:horizontal {
  background-color: white;
    border-radius: 1px;
    border:1px solid dimgray;
}

::-webkit-scrollbar-thumb:hover {
    background: royalblue !important;
}

::-webkit-scrollbar-thumb:active {
    background: orange !important;
}

::-webkit-scrollbar-track:vertical, ::-webkit-scrollbar-track:horizontal {
  background-color: transparent;
}

::-webkit-scrollbar-corner {
  background-color: dimgray;
}
 
::-webkit-scrollbar-button:start:decrement,
::-webkit-scrollbar-button:end:increment {
  display: none;
}
 
