css:
@-moz-document domain("www.heise.de") {
div#adb-note,
div.channel-logo,
div.readspeaker-link,
#bannerzone,
#themen_aktuell,
#mitte_rechts,
aside.recommendations,
aside.row.channel.channel_ho,
div.adbottom.clearfix,
.us_ad,div.link_forum_beitrag.news,
footer.article-footer,.themenseiten,ol.breadcrumb,p.news_navi,
div.buehnenteaser.clearfix,
#navi_bottom,
#sitemap,#logo_bereich,div.heisetopnavi.v14,
aside[aria-label="Preisvergleich"]
{display:none !important;}

.article-header .publish-info {
color: rgba(240,240,240,1);
}

#container {
    position: static !important;
    float: none !important;
    width: 40rem !important;
    min-width: 40rem !important;
    margin: 0 auto;
    /*width: 500px !important;
    max-width: 500px !important;*/
}
#container #container_content {
    float: none;
    top: 0;  
}
body {
    color: rgba(240,240,240,1);
    background-color: rgba(20,20,20,1);
}
a:link, a:hover, a:visited, a:active {
	color: rgba(240,240,240,1);
}
#mitte {
    max-width: 25rem;
	float:left;
    color: rgba(240,240,240,1);
    background-color: rgba(20,20,20,1);
}
#mitte #mitte_links {
  width: 100%;
}

html, body, article, div, h1, h2, h3, h4, h5, p, ul, ol, dl, span, em, a, form, #article, b, ul, ol, dl, pre, li, dd, dt, table, tr, td, th, #content, strong {
    font-family: 'Source Sans Pro', Arial, sans-serif !important;
    color: rgba(240, 240, 240, 1) !important;
    background-color: rgba(20, 20, 20, 1) !important;
    background-image: none;
    line-height: 1.4 !important;
}

.author-info dl {
    overflow: visible;
}
/* etwas bessere lesbarkeit */

html, body, form {
    font-size: 20px;
}

h1 {
    font-size: 1.6rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
    font-weight: 700;
    line-height: 1.2;
}

h2 {
    font-size: 1.4rem !important;
    font-weight: 600;
    margin-top: .7rem;
    margin-bottom: .1rem;
    line-height: 1.2;
}

h3 {
    font-size: 1.2rem !important;
    font-weight: 500;
    line-height: 1.2 !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
}

h4 {
    font-size: 1.1rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
}

h5 {
    font-size: 1rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
}

p, a, ul, li, dl, dd, dt, ol, div, pre, blockquote, .article-content.clearfix {
    font-size: 1rem !important;
    font-weight: 300 !important;
    margin-top: .1rem;
    margin-bottom: .7rem;
    line-height: 1.5 !important;
}

strong {
    font-weight: 500;
}

a:active, a:hover, a:link, a:visited {
    color: rgba(240, 240, 240, 1);
    text-decoration: underline;
}

.dossier, .heisebox, .heisebox_left, .heisebox_right {
	  width: 100%;
  font-style: italic;
    float:none;
}
.heisebox_left h1,.heisebox_left p,.heisebox_left ul,
.heisebox_right h1,.heisebox_right p,.heisebox_right ul {
	font-size: .8rem !important;
    
}
}
