css:
@-moz-document domain("mactechnews.de") {

/* All das zeugs das ich nicht sehen will weg */
body > div,
body>header,
body>p,
body>ul,
body>footer,
#comments,
#ContentPlaceHolder1_MtnNewsCommentScrollNewsComments,
.SynMessageNote,
*+h2.Title,
*+h2.TitleWithNavigation,
.SynFacebookLike,
.SynTwitterShare,
#content
aside {
    display: none;
}
/* iframes sind wohl immer ads */
#adContent,#MTNN_Unterhalb_Artikel_Neu,ins.adsbygoogle,div#googlenewsarticle
iframe {
    display: none !important;
}

/* den content selber wider unhiden */
article {
    display: block;
    width: 25rem !important;
     max-width: 25rem !important;
}
/* Schrift unf Farbe fuer alles */
html,body,article,div,h1,h2,h3,h4,h5,p,ul,
ol,dl,span,em,a,form {
    font-family: 'Source Sans Pro',Arial,sans-serif;
    color: rgba(240, 240, 240, 1) !important;
    background-color: rgba(20, 20, 20, 1) !important;
}
/* haessliche weisse borders um bilder weg */
.TexticonThumbPicture,
.TexticonUrlPicture {
    border: none !important;
}

/* etwas bessere lesbarkeit */
html, body, form {
	font-size: 16px;
}
h1 {
    font-size: 2em !important;
}
h2 {
    font-size: 1.8em !important;
}
h3 {
    font-size: 1.6em !important;
}
h4 {
    font-size: 1.4em !important;
}
h5 {
    font-size: 1.3em !important;
}
p {
    font-size: 1em !important;
}
a:active,
a:hover,
a:link,
a:visited {
    color: rgba(240,240,240,1);
    text-decoration: underline;
}
}
