css:
@-moz-document domain("macrumors.com") {
#header,#commentsContainer,#right, div.follow-banner,#fContainer,.comments{
display:none;
}
div.article {
border: none;
}

*,* *,blockquote,.article .content blockquote,.article .content a,.article .content ul, .article .content ul li, .article .content table, .article .content table tr, .article .content table tr td
{
	font-size: 22px;
    line-height: 1.5;
}
.article,body,html,h1,h2,h3,h4,h5,p,span,ul,li,ol,form,div,a,blockquote,span {
    font-family: 'Source Sans Pro',Arial,sans-serif;
    background-image: none; 
    color: rgba(240,240,240,1) !important;
	background-color: rgba(20,20,20,1) !important;
}
h1.title {
font-size: 1.5em !important;
}
div.content {
 font-size: 1em !important;
}
}
