css:
@-moz-document domain("t3n.de") {
/* Contenthiding */
body > div, body>p, body>header,body>footer,.ob-widget-section.ob-first,
#headMain,#head,.OUTBRAIN,aside.contentInnerGray,
div.contentDivider.cf,.socialicons__bar,.columnSet3.fullColumn.contentDivider2.cf,
.stageIssues,#footInner,aside.sidebar,.switch-article__navigation.switch-article__navigation--footer,
.contentInner.contentDivider,.seo-headline,section.newsletterbox
{
	display:none;
}
/* AdHiding */
#adunit,.ads,.content-ads,iframe,div[id^='google_ads_iframe'],#Ads_BA_FOOT,div.digidip.js-textadplus-container,
#Ads_BA_CAD2,div[id^='guruads_zone'],.icon-external-link,a>i,.postHeadImage,.article__author.cf,
.postHeadMeta,.digidip,span[id^='more'],.socialicons__bar.socialicons__bar--header,.narando-player
{
	display:none !important;
    margin:0 !important;
    padding:0 !important;
    border: none !important;
}

/* Schrift unf Farbe fuer alles */
/*html,body,article,header,#pageWrapper,#main-content,#post-703429 > header > div.teaser > p:nth-child(1)*/
article[id^='post'],article[id^='post'] * ,
body,#stage
{
    font-family: 'Source Sans Pro',Arial,sans-serif;
    color: rgba(240, 240, 240, 1) !important;
    background-color: rgba(20, 20, 20, 1) !important;
	line-height: 1.5 !important;
}
#stage {
	border: none;
}
/* etwas bessere lesbarkeit */
html,body,article,article * {
	font-size: 24px;
        line-height: 1.5;
}
h1 {
    font-size: 1.7rem !important;
}
h2 {
    font-size: 1.5rem !important;
}
h3 {
    font-size: 1.3rem !important;
}
h4 {
    font-size: 1.2rem !important;
}
h5 {
    font-size: 1.1rem !important;
}
p {
    font-size: 1rem !important;
    line-height: 1.5;
}
a:active,
a:hover,
a:link,
a:visited {
    font-size: 1rem;
    color: rgba(240,240,240,1);
    text-decoration: underline;
    line-height: 1.5;
}
.entry h2 {
  margin: 0 0 .2rem;
  font-size: 22px;
  line-height: 26px;
}
.articleWrapper.hasCategoryAd .postHead {
  margin-top: 0;
  padding-top: 0;
}
}
