css:
@-moz-document domain("tagesanzeiger.ch") {
/* Contenthiding */
#sidebarSky,#headerWrapper,#mainColRight,#mainColBottom,#mainColBottomMore,#footer>div#footer,#bottomNav,#footerLinks,
div.articleSharing.articleSharing_box,#aside
{
	display:none;
}
#wideBoardContainer,#ppninnerbox,div.singlebox.bottom.left.right,#Stage_link,#Stage,
.tt-wrapper.inread 
{
	display:none !important;
}

#article {
	width: 30rem;
    margin: 0 auto;
    float: none;
}

/* Schrift unf Farbe fuer alles */
html,body,article,div,h1,h2,h3,h4,h5,p,ul,
ol,dl,span,em,a,form,#article,b,
ul,ol,dl,pre,li,dd,dt,table,tr,td,th
{
    font-family: 'Source Sans Pro',Arial,sans-serif !important;
    color: rgba(240, 240, 240, 1) !important;
    background-color: rgba(20, 20, 20, 1) !important;
}
/* haessliche weisse borders um bilder weg */
.TexticonThumbPicture,
.TexticonUrlPicture {
    border: none !important;
}

/* etwas bessere lesbarkeit */
html, body, form {
	font-size: 22px;
}
h1 {
    font-size: 2rem !important;
    
    font-weight: 800;
}
h2 {
    font-size: 1.4rem !important;
    font-weight: 700;
}
h3 {
    font-size: 1.2rem !important;
    font-weight: 600;
    line-height: 1.2 !important;
}
h4 {
    font-size: 1.3rem !important;
}
h5 {
    font-size: 1.1rem !important;
}
p {
    font-size: 1rem !important;
}
a:active,
a:hover,
a:link,
a:visited {
    color: rgba(240,240,240,1);
    text-decoration: underline;
}
#mainContent {
  float: none;
  width: 100%;
    line-height: 1.5;
}
.pictureVotingButtons {
 line-height: 1;
    width: inherit;
    height:inherit;
    margin: 0;
    padding: 0;
}
}
