css:
@-moz-document domain("nzz.ch") {
.l--premain,header.l--header,section.container.container--topnews,
footer.l--footer,aside.resor.resor--billboard,.sharebox,aside.l--aside,.metainfo--content:after,.leadtext:after,
.title--content:after
{
    display: none;
}


header.content__header,article.content,article.content main,div.card
{
	width: 30rem;
    overflow:visible;
    margin: 0 auto;
    float: none;
}

.page--article .content__header, .page--weather .content__header {
	margin-right: 0 !important;
}

/* Schrift unf Farbe fuer alles */
html,body,article,div,h1,h2,h3,h4,h5,p,ul,
ol,dl,span,em,a,form,#article,b,
ul,ol,dl,pre,li,dd,dt,table,tr,td,th
{
    font-family: 'Source Sans Pro',Arial,sans-serif !important;
    color: rgba(240, 240, 240, 1) !important;
    background-color: rgba(20, 20, 20, 1) !important;
}
/* haessliche weisse borders um bilder weg */
.TexticonThumbPicture,
.TexticonUrlPicture {
    border: none !important;
}

/* etwas bessere lesbarkeit */
html, body, form {
	font-size: 22px;
    line-height: 1.5 !important;
}
h1 {
    font-size: 2rem !important;
    margin-top: .7rem;
    margin-bottom: .1rem;
    font-weight: 800;
}
h2 {
    font-size: 1.4rem !important;
    font-weight: 700;
        margin-top: .7rem;
    margin-bottom: .1rem;
}
h3 {
    font-size: 1.2rem !important;
    font-weight: 600;
    line-height: 1.2 !important;
        margin-top: .7rem;
    margin-bottom: .1rem;
}
h4 {
    font-size: 1.3rem !important;
        margin-top: .7rem;
    margin-bottom: .1rem;
}
h5 {
    font-size: 1.1rem !important;
        margin-top: .7rem;
    margin-bottom: .1rem;
}
p, a {
    font-size: 1rem !important;
    font-weight: 300 !important;
    margin-top: .1rem;
    margin-bottom: .7rem;
}
a:active,
a:hover,
a:link,
a:visited {
    color: rgba(240,240,240,1);
    text-decoration: underline;
}
}
